/********************************************************************************
* 기      능   : Calendar 관련 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {

	//alert(ds_list.GetColumn(nRow, "GB"));

	//문서관리번호 신규추가시 NULL , 수정시 저장된 해당 문서관리번호
	var a=ds_list.GetColumn(nRow, "CS655_DOC");  
	//오더유형코드 //1:정기보수 //2:수리공사 //3:부품판매
	var b="";
	if(length(ds_list.GetColumn(nRow, "CS655_OKN"))>0){
		b=ds_list.GetColumn(nRow, "CS655_OKN");
	} else {
		b="1";
	}
	//문서구문코드 //1:계약서 //2:발주서 //3:계약변경합의서 //4:작업완료확인서 //5:추가약정서 
	//var c=ds_list.GetColumn(nRow, "CS655_DKN"); 

	var arg = "";
	arg = " fa_docno="+quote(a);
	arg = arg +" fa_oknno="+quote(b);
	//arg = arg +" fa_dknno="+quote(c);
	if(ds_list.GetColumn(nRow, "GB")=="1"){
		var rtn_doc = gfn_openPopup2("CS9615001B", true , arg);
		if(length(ds_list.GetColumn(nRow, "CS655_DOC"))<1 && length(rtn_doc)>10) {
				ds_doc.ClearData();
				ds_doc.AddRow();																
				ds_doc.SetColumn(0,"CS126_UPN",ds_list.GetColumn(nRow, "CS126_UPN"));
				ds_doc.SetColumn(0,"CS126_CST",ds_list.GetColumn(nRow, "CS126_CST"));	
				ds_doc.SetColumn(0,"CS126_GNO",ds_list.GetColumn(nRow, "CS126_GNO"));	
				ds_doc.SetColumn(0,"CS126_SEQ",ds_list.GetColumn(nRow, "CS126_SEQ"));	
				ds_doc.SetColumn(0,"CS126_DNO",rtn_doc);	

				tit_clearActionInfo();

				//저장 처리 
				tit_addMultiActionInfo("cs96:CS9615001A_U1");
				//trace(ds_head.SaveXML());

				tit_callService(
					""
					, ""
					, "ds_doc=ds_doc:A"
					, ""
					, ""
					, ""
					, true
					, true
					, true);

			}
			//alert(rtn_doc); // return된 문서 관리번호를 저장하면 됨.
	} else {
		var rtn_doc = gfn_openPopup2("CS9615001C", true , arg);
		if(length(ds_list.GetColumn(nRow, "CS655_DOC"))<1 && length(rtn_doc)>10) {
				ds_doc.ClearData();
				ds_doc.AddRow();																
				ds_doc.SetColumn(0,"CS126_UPN",ds_list.GetColumn(nRow, "CS126_UPN"));
				ds_doc.SetColumn(0,"CS126_CST",ds_list.GetColumn(nRow, "CS126_CST"));	
				ds_doc.SetColumn(0,"CS126_GNO",ds_list.GetColumn(nRow, "CS126_GNO"));	
				ds_doc.SetColumn(0,"CS126_SEQ",ds_list.GetColumn(nRow, "CS126_SEQ"));	
				ds_doc.SetColumn(0,"CS126_DNO",rtn_doc);	

				tit_clearActionInfo();

				//저장 처리 
				tit_addMultiActionInfo("cs96:CS9615001A_U2");
				//trace(ds_head.SaveXML());

				tit_callService(
					""
					, ""
					, "ds_doc=ds_doc:A"
					, ""
					, ""
					, ""
					, true
					, true
					, true);
		}
	}
}



function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 12 && length(ds_list.getColumn(nRow, "CS655_NAM")) > 0 && ds_list.getColumn(nRow, "CS655_SIZ") > 0) {
		// 첨부 파일 처리 
		saveDialog.FileName = tit_getLocalFileName(ds_list.getColumn(nRow, "CS655_NAM"));
		if (saveDialog.open())
		{
			tit_fileRead(
				httpFile
				, "CS"
				, ds_list.getColumn(nRow, "CS655_NAM")
				, saveDialog.FilePath
				, saveDialog.FileName);
		}
	}

}
