#include "LIB::commons.js";           // 공통 스크립트
#include "LIB::fs_common.js";         // 물류서비스 공통 스크립트

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
    gfn_initForm();         // Form 로드 이벤트 맨 첫줄에 기술
    fn_init_form();             // 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
    //return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	gfn_initDs( ds_cond , true );
    ds_cond.SetColumn(0, "ADOC_CLAS", adocClas );
    ds_cond.SetColumn(0, "ADOC_NUMB", adocNumb );
	var codeArray = [
			"A06:ds_combo_A06"
		];
    // 공용코드 콤보 데이터 셑을 가져온다.
    gn_fsComboDataSet(codeArray);
    SetTimer(1, 300);
}

/********************************************************************************
* 기      능   : 조회 딜레이
********************************************************************************/
function fn_OnTimer(obj,nEventID)
{
    fn_query();
    KillTimer(nEventID);
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {

    // 조회 스크립트
    tit_clearActionInfo();
    tit_addSearchActionInfo("fs01:FS0103001B_S1");
    tit_callService(
                    ""
                   ,""
                   ,"ds_cond=ds_cond"
                   ,"ds_zfst0104=ds_zfst0104"
                   ,""
                   ,"fn_afterQuery"
                   ,"true"
                   ,"true"
                   ,"false"
                   );
}
function fn_afterQuery(errCode, errMsg) 
{
    gfn_barMsgSearch( ds_zfst0104.RowCount() , errCode); 
    // 조회 스크립트
    ds_zuserf_list.ClearData();
    tit_clearActionInfo();
    tit_addSearchActionInfo("fs01:FS_COM0801_S1");
    tit_callService(
                    ""
                   ,""
                   ,"ds_cond=ds_cond_zuserf"
                   ,"ds_zuserf_list_temp=ds_zuserf_list"
                   ,""
                   ,"fn_initList"
                   ,"true"
                   ,"true"
                   ,"false"
                   );
}

function fn_initList(errCode, errMsg) 
{
	var nAddedRow = -1;
	var nRowCount = ds_zfst0104.GetCount();
	for(var i=0; i < nRowCount; i++)
	{
		nAddedRow = ds_zuserf_list_temp.FindRow("USERNUMB", ds_zfst0104.GetColumn(i, "MNGR"));
		if (nAddedRow >= 0)
		{
			ds_zuserf_list_temp.DeleteRow(nAddedRow);
		}
	}
	ds_zuserf_list.Copy(ds_zuserf_list_temp);
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if ( gfn_showConfirm("CI00008") ) 
	{

		var nFindRow = ds_zfst0104.FindRow("MNGR_GBN", "");
		if ( nFindRow >= 0 )
		{
			gfn_showAlert("담당자 역확을 선택해야 합니다.");
			ds_zfst0104.Row = nFindRow;
			return;
		}

		tit_clearActionInfo();
		tit_addSaveActionInfo("fs01:FS0103001B_I1","fs01:FS0103001B_U1","fs01:FS0103001B_D1"); 

		tit_callService(
						""
					   ,""
					   ,"ds_zfst0104=ds_zfst0104:A"
					   ,""
					   ,""
					   ,"fn_afterSave"
					   ,"true"
					   ,"true"
					   ,"false"
					   );
	}
}

function fn_afterSave(errCode, errMsg)
{
	if( errCode != 0 ) {
		gfn_showMsg("CE00001");
		return true;
	}
	gfn_showMsg("CI00003");
	fn_query();
}
/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
    gfn_formClose( ds_zfst0104.GetUpdate() );
}

/********************************************************************************
* 기      능   : 담당자 추가
********************************************************************************/
function grd_user_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	btn_rowAdd_OnClick(obj);
}

/********************************************************************************
* 기      능   : 정렬
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	gfn_sortGridFS(obj,nCell);
}

/********************************************************************************
* 기      능   : 담당자 제거
********************************************************************************/
function grd_list_CellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var strColumnID = grd_list.GetCellProp("body", nCell, "colid");
	if ( strColumnID=="MNGR_NAME" ) btn_rowDel_OnClick(obj);
}

/********************************************************************************
* 기      능   : 담당자 추가
********************************************************************************/
function btn_rowAdd_OnClick(obj)
{
	var nRow = ds_zuserf_list.GetRowPos();
	if ( ds_zfst0104.FindRow("MNGR", ds_zuserf_list.GetColumn(nRow, "USERNUMB")) >= 0 )
	{
		gfn_showAlert("이미 추가된 담당자 입니다.");
	}
	else
	{
		var iRow = ds_zfst0104.AppendRow();
		ds_zfst0104.SetColumn( iRow , "ADOC_CLAS" , adocClas );
		ds_zfst0104.SetColumn( iRow , "ADOC_NUMB" , adocNumb );
		ds_zfst0104.SetColumn( iRow , "MNGR" , ds_zuserf_list.GetColumn( nRow , "USERNUMB" ) );
		ds_zfst0104.SetColumn( iRow , "MNGR_NAME" , ds_zuserf_list.GetColumn( nRow , "USERNAME" ) );
		ds_zfst0104.SetColumn( iRow , "MNGR_GBN" , "" );
	}
	//ds_zuserf_list.DeleteRow( nRow );
}

/********************************************************************************
* 기      능   : 담당자 제거
********************************************************************************/
function btn_rowDel_OnClick(obj)
{
	ds_zfst0104.DeleteRow( ds_zfst0104.GetRowPos() );
}
