//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
    gfn_initForm(true);             // Form 로드 이벤트 맨 첫줄에 기술
    fn_init_form();             // 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
    return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
    // 공용코드 데이터 코드 타입: CODE_GROP:DATASETID -> A02:ds_combo_a02, A03:ds_combo_a03
    //var codeArray = ["A03:ds_combo_a03"];
    // 공용코드 콤보 데이터 셑을 가져온다.
    // gn_fsComboDataSet(codeArray);
    ds_combo_a03.ClearData();
    ds_combo_a03.Copy(Parent.ds_combo_a03);

    // 현재 시스템 데이터 조회
    var sysData = gfn_getCurrDate();
    var writYefr = substr(sysData, 0,4);
    var writYeto = toString(toNumber(writYefr)+1);
    ds_cond.SetColumn(0, "WRIT_YEFR", writYefr);
    ds_cond.SetColumn(0, "WRIT_YETO", writYeto);

    // 전달 파라미터 값 셑팅
    ds_cond.SetColumn(0, "ESCS_NUMB", escsNumb);
    ds_cond.SetColumn(0, "ESCS_NTIM", escsNtim);
    ds_cond.SetColumn(0, "KUNNR", kunnr);
    ds_cond.SetColumn(0, "KUNNR_NAME", kunnrName);
    ds_cond.SetColumn(0, "ESCS_ITEM", escsItem);
    ds_cond.SetColumn(0, "EXTN_ESCS", extnEscs);
    ds_cond.SetColumn(0, "ESCE_NAME", esceName);

    fn_query();
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
    gfn_clearGridSortMark(grd_detail);

    // 조회 스크립트
    // 예산코드 검색 조건을 정의 ESCS_ITEM
    // CODE CODE2 CODE3
    if( ed_kunnrName.Text == "" ) {
		gfn_openMsgWin("고객정보 검색조건을 입력해 주세요", "", "A", "");
		return;
    }

    tit_clearActionInfo();
    tit_addSearchActionInfo("fs02:FS0201002D_S1");
    tit_callService(
                    ""
                   ,""
                   ,"ds_cond=ds_cond"
                   ,"ds_zfst0201_list=ds_zfst0201_list"
                   ,""
                   ,"fn_afterQuery"
                   ,"true"
                   ,"true"
                   ,"false"
                   );
}

/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
    if(ds_error.rowcount() > 0) {
        gfn_showMsg("CE00001");
    } else {
		//gfn_openMsgWin(ds_zfst0201_list.GetRowCount()+"건이 조회 되었습니다.", "", "A", "");
    }
}

/********************************************************************************
* 기      능   : 복사
********************************************************************************/
function fn_copy() 
{
    //trace("chk cnt:"+ds_zfst0201_list.CaseCountNF("CHK='1'"));
    if( ds_zfst0201_list.CaseCountNF("CHK='1'") == 0 ) {
		gfn_openMsgWin("선택된 품목이 없습니다.", "", "A", "");
		return;
    }

	if( gfn_showConfirm("견적 품목을 복사 하시겠습니까?") ) {
		var copyCnt = 0;
		Parent.fn_rowCopyStart();
		for(var iRow=0; iRow < ds_zfst0201_list.GetRowCount(); iRow++) {
			if( ds_zfst0201_list.GetColumn(iRow, "CHK") == "1" ) {
				var extnEscs = ds_zfst0201_list.GetColumn(iRow, "EXTN_ESCS");
				var nRow = Parent.fn_masterRow(extnEscs);
				if( nRow > - 1 ) {
					Parent.fn_rowCopy(
									  nRow
									 ,extnEscs
									 ,ds_zfst0201_list.GetColumn(iRow, "MTRL_NUMB")
									 ,ds_zfst0201_list.GetColumn(iRow, "PART_NAME")
									 ,ds_zfst0201_list.GetColumn(iRow, "STAD")
									 ,ds_zfst0201_list.GetColumn(iRow, "UNIT")
									 ,ds_zfst0201_list.GetColumn(iRow, "QNTY")
									 ,ds_zfst0201_list.GetColumn(iRow, "WAERK") 
									 ,ds_zfst0201_list.GetColumn(iRow, "UKURS") 
									 ,ds_zfst0201_list.GetColumn(iRow, "COST") 
									 ,ds_zfst0201_list.GetColumn(iRow, "ICDT_EPNS") 
									 ,ds_zfst0201_list.GetColumn(iRow, "IDRT_COST") 
									 ,ds_zfst0201_list.GetColumn(iRow, "SALE_COST") 
									 ,ds_zfst0201_list.GetColumn(iRow, "NGTT_ESCS") 
									 ,ds_zfst0201_list.GetColumn(iRow, "ESMT_CMPY")
									 );
					copyCnt = copyCnt + 1;
				}
			}
		}

		// 총괄 합계를 계산한다.
		Parent.fn_rowCopySum();
		Parent.fn_rowCopyEnd();
		gfn_openMsgWin("총 "+copyCnt+"건이 복사 되었습니다.", "", "A", "");

		close();
    }
}

/********************************************************************************
* 기      능   : 상위 로우를 리턴한다.
********************************************************************************/
function fn_getUpRow(pescsUpgr) {
    return ds_zfst0201_list.FindRow("PESCS_GROP", pescsUpgr);
}

/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
    close();
}

/********************************************************************************
* 기      능   : 고객정보 팝업을 호출한다.
********************************************************************************/
function btn_cusnName_OnClick(obj)
{
	var arg="";
	var arr=null;
    arr = gfn_openPopup("FS_COM0201", true, arg);
    ds_cond.fireevent = false;
    if( length(arr) > 0 )  {
        ds_cond.SetColumn(0, "KUNNR", arr[0]);
        ds_cond.SetColumn(0, "KUNNR_NAME", arr[1]);
    }
    ds_cond.fireevent = true;
}

/********************************************************************************
* 기      능   : 견적원가 조회 팝업을 호출한다.
********************************************************************************/
function btn_budget_num_OnClick(obj)
{
    var arg="";
    var arr=null;
    arr = gfn_openPopup("FS0201001B", true, arg);
    if( length(arr) > 0 ) {
		ds_cond.SetColumn(0, "CESCS_NUMB", arr[0]);
		ds_cond.SetColumn(0, "CESCS_NTIM", arr[1]);
    }
}

