#include "LIB::commons.js";            // 공통 스크립트
#include "LIB::fs_common.js";         // 물류서비스 공통 스크립트

//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
    gfn_initForm();             // Form 로드 이벤트 맨 첫줄에 기술
    fn_init_form();             // 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
    // drinlDown 컴포넌트 인스턴스 생성
   	gfn_InitDrillDown(ds_zfst0101, grd_master, "true", "FLAG", "LEVEL", "BDGT_NUMB", "BDGT_UPNB", "BDGR_NAME", "CHK");
   	if( escsNumb != "" ) {
        ds_cond.SetColumn(0, "ESCS_NUMB", escsNumb);
		ds_cond.SetColumn(0, "ESCS_NTIM", escsNtim);
   	}
    fn_query();
}

/********************************************************************************
* 기      능   : 예산그룹관리 조회
********************************************************************************/
function fn_query() {
    // 조회 스크립트
    tit_clearActionInfo();
    tit_addSearchActionInfo("fs02:FS0201002F_S2");
    tit_callService(
                    ""
                   ,""
                   ,"ds_cond=ds_cond"
                   ,"ds_zfst0101=ds_zfst0101"
                   ,""
                   ,"fn_afterQuery"
                   ,"true"
                   ,"true"
                   ,"true"
                   );
}

/********************************************************************************
* 기      능   : 예산그룹관리 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
    if(ds_error.rowcount() > 0) {
        gfn_showMsg("CE00001");
    } else {
        gfn_showMsg("CI00002", ds_zfst0101.rowCount() + "");
    }
}

/********************************************************************************
* 기      능   : 예산그룹관리 저장
********************************************************************************/
function fn_save() {
    if (gfn_showConfirm("견적원가 예산항목을 등록하시겠습니까?")) {
        var iRow = 0;
        for(var i=0; i < ds_zfst0101.GetRowCount(); i++) {
            var chk = ds_zfst0101.GetColumn(i, "CHK");
            var extnEscs = ds_zfst0101.GetColumn(i, "EXTN_ESCS");
            // 선택된 코드 및 상위코드 중 선택하지 않았으나 등록된 값이 없는 경우 항목을 추가해 준다.
            if( length(chk) > 0 &&  extnEscs == "" ) {
                // 예산품목 정보를 등록한다.
                var nRow = ds_zfst0201_list.AppendRow();
                // 세부품목여부
                ds_zfst0201_list.SetColumn(nRow, "ITEM_DIYN", "M");
                // 견적원가그룹
                ds_zfst0201_list.SetColumn(nRow, "ESCS_GROP", ds_zfst0101.GetColumn(i, "BDGT_NUMB"));
                // 상위견적원가그룹
                ds_zfst0201_list.SetColumn(nRow, "ESCS_UPGR", ds_zfst0101.GetColumn(i, "BDGT_UPNB"));
                // 견적원가항목
                ds_zfst0201_list.SetColumn(nRow, "ESCS_ITEM", ds_zfst0101.GetColumn(i, "ESCS_CODE"));
                // 견적원가항목코드
                ds_zfst0201_list.SetColumn(nRow, "EXTN_ESCS", ds_zfst0101.GetColumn(i, "BGIT_CODE"));
                // 견적원가명
                ds_zfst0201_list.SetColumn(nRow, "ESCE_NAME", ds_zfst0101.GetColumn(i, "BDGR_NAME"));
                // 견적원가 등록 순번
                ds_zfst0201_list.SetColumn(nRow, "ESIT_SEQN", ds_zfst0101.GetColumn(i, "BDGR_CDSQ"));
            }
            iRow = iRow +1;
        }

        if(ds_zfst0201_list.GetRowCount() == 0 ) {
            gfn_openMsgWin("선택된 예산항목이 없습니다.", "", "A", "");
            return;
        }

        ds_zfst0201.ClearData();
		tit_clearActionInfo();
		// 서비스 체크 로직은 별도 Act를 호출하여 처리한다.
		tit_callService(
			  "FS0201002A_ACT"
			, "escsMasterSave"
			, "ds_cond=ds_cond ds_zfst0200=ds_zfst0200 ds_zfst0201=ds_zfst0201 ds_zfst0201_list=ds_zfst0201_list"
			, "ds_zfst0200=ds_zfst0200 ds_error=ds_error"
			, ""
			, "fn_afterSave"
			, true
			, true
			, true);
		}
	}
}

/********************************************************************************
* 기      능   : 예산그룹관리 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
    if( errCode != 0) {
        gfn_showMsg("CE00001");
    } else {
        gfn_openMsgWin("견적원가 예산항목이 생성 되었습니다.", "", "A", "");
        gfn_showMsg("CI00003");
        if( length(escsNumb) > 0 ) {
			var arr = [
				  ds_cond.GetColumn(0,"ESCS_NUMB")
				, ds_cond.GetColumn(0,"ESCS_NTIM")
			];
			close(arr);
        } else {
			var arr = [
				  ds_zfst0200.GetColumn(0,"ESCS_NUMB")
				, ds_zfst0200.GetColumn(0,"ESCS_NTIM")
			];
			close(arr);
        }
    }
}

/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
    close();
}

/********************************************************************************
* 기      능   : 그리드 정렬기능
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	var colid = grd_master.GetCellProp("head", nCell, "ColId");
	switch(colid){
		case "CHK":
			var nCount = grd_master.rowcount;
			var checkedCount = grd_master.CaseCount("CHK=='1'");
			var checked = nCount == checkedCount;
			for(var iRow = 0; iRow < nCount; iRow++){
				grd_master.SetColumn(iRow, colid, iif( checked , "", "1"));
			}
			obj.Redraw("head");
		break;
	}
	if (nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		//gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		//gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 접기 와 펼치기
********************************************************************************/
function grd_master_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var columnID = obj.GetCellProp("body",nCell,"colid");
	var value = obj.GetCellValue(nRow, nCell);
	switch ( columnID ){
	case "CHK":
	    if( value != "0" ) {
		    gfn_DrillDownCheckBox(obj, nRow);
	    }
		break;
	case "FLAG":
		ds_zfst0101_f.SetColumn(nRow, columnID, iif( value == '1' , '0', '1' ));
		ds_zfst0101_f.RecalcFilter();
		break;
	}
}
