#include "LIB::commons.js";           // 공통 스크립트
#include "LIB::fs_common.js";         // 물류서비스 공통 스크립트

//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
    gfn_initForm(true);         // Form 로드 이벤트 맨 첫줄에 기술
    fn_init_form();             // 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
    return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	fn_btnControll();
	cal_writStdt.Text = gfn_getBeforeMonth( Mid( gfn_getCurrDate() , 0, 6 ) , 1 )  + "01" ;
	cal_writEddt.Text = gfn_getCurrDate();
	var codeArray = 
		[
			 "A02:ds_combo_a02::EXTN2="+Quote("ES")
			,"A03:ds_combo_a03::EXTN1="+Quote("_%")
		];
    // 공용코드 콤보 데이터 셑을 가져온다.
    gn_fsComboDataSet(codeArray);
    SetTimer(1, 300);
}

/********************************************************************************
* 기      능   : 폼 타이머
********************************************************************************/
function fn_OnTimer(obj,nEventID)
{
	KillTimer(nEventID);
	fn_query();
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	if (!gfn_checkFromToObj(cal_writStdt, cal_writEddt, true, "견적서작성일", 8, true)) return;
	gfn_initDs( ds_cond , true );
	ds_cond.SetColumn( 0, "ESMT_NUMB", ed_esmtNumb.Text); 
	ds_cond.SetColumn( 0, "BSNS_NAME", ed_bsnsName.Text);
	ds_cond.SetColumn( 0, "KUNNR", ed_orcuName.UserData);
	ds_cond.SetColumn( 0, "ORCU_NAME", ed_orcuName.Text);
	ds_cond.SetColumn( 0, "ESMT_CNTR", ed_esmtCntr.UserData);
	ds_cond.SetColumn( 0, "ESMT_STAT", cb_esmtStat.Value);
	ds_cond.SetColumn( 0, "ESCS_NUMB", ed_escsNumb.Text);
	ds_cond.SetColumn( 0, "ESCS_NTIM", "");
	ds_cond.SetColumn( 0, "WRIT_STDT", cal_writStdt.Value);
	ds_cond.SetColumn( 0, "WRIT_ENDT", cal_writEddt.Value);
	ds_cond.SetColumn( 0, "ORDR_TYPE", cb_ordrType.Value);
	ds_cond.SetColumn( 0, "SALE_PRSN", ed_salePrsn.UserData);
    // 조회 스크립트
    tit_clearActionInfo();
    tit_addSearchActionInfo("fs02:FS0202001A_S1");
    tit_callService(
                    ""
                   ,""
                   ,"ds_cond=ds_cond"
                   ,"ds_zfst0202=ds_zfst0202"
                   ,""
                   ,"fn_afterQuery"
                   ,"true"
                   ,"true"
                   ,"false"
                   );
}
function fn_afterQuery( errorCode, errorMsg )
{
	gfn_barMsgSearch( ds_zfst0202.GetRowCount(), errorCode ); 
}

/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
    close();
}

/********************************************************************************
* 기      능   : 견적원가번호 조회 팝업을 호출한다.
********************************************************************************/
function btn_escsNumb_OnClick(obj)
{
	var arg="";
    var aRtn = gfn_openPopup("FS0201001B", true, arg);
    if ( length( aRtn ) > 0 )
    {
		ed_escsNumb.Text = aRtn[0];
    }
}

/********************************************************************************
* 기      능   : 견적서 등록/변경 화면을 호출한다.
********************************************************************************/
function btn_esmtDoc_OnClick(obj)
{
	var arg = " pEscsNumb='' pEscsNtim=''";
	var nRow = ds_zfst0202.GetRowPos(); 
	if ( nRow >= 0 )
	{
		var esmtNumb = ds_zfst0202.GetColumn( nRow , "ESMT_NUMB");
		arg += " pEsmtNumb="+Quote(esmtNumb);
	}
	else
	{
		arg += " pEsmtNumb=''";
	}
    gfn_openWin("FS0202003A", arg);
}

/********************************************************************************
* 기      능   : 사원 조회 화면을 호출한다.
********************************************************************************/
function btn_salePrsn_OnClick(obj)
{
    var arg = "";
    var aRtn = gfn_openPopup("FS_COM0801", true, arg);
    if ( length( aRtn ) > 0 )
    {
		ed_salePrsn.UserData = aRtn[0];
		ed_salePrsn.Text = aRtn[1];
    }
}

/********************************************************************************
* 기      능   : 고객 조회 화면을 호출한다.
********************************************************************************/
function btn_cusnName_OnClick(obj)
{
	var arg="";
	var arr=null;
    arr = gfn_openPopup("FS_COM0201", true, arg);
    ds_cond.fireevent = false;
    if( length(arr) > 0 )  {
        if( arr[0] != "" ) {
			ds_cond.SetColumn(0, "KUNNR", arr[0]);
			ed_orcuName.UserData = arr[0];
			ed_orcuName.Text = arr[1];
        } else {
			ds_cond.SetColumn(0, "KUNNR", "");
			ed_orcuName.UserData = "";
			ed_orcuName.Text = "";
        }
    }
    ds_cond.fireevent = true;
}

/********************************************************************************
* 기      능   : 고객 명 검사 기준 설정
********************************************************************************/
function ed_orcuName_OnChanged(obj,strText)
{
	ed_orcuName.UserData = "";
}

/********************************************************************************
* 기      능   : 국가 조회 화면을 호출한다.
********************************************************************************/
function btn_esmtCntr_OnClick(obj)
{
    var arg = "";
    var aRtn = gfn_openPopup("FS_COM0901", true, arg);
    if ( length( aRtn ) > 0 )
    {
		ed_esmtCntr.UserData = aRtn[0];
		ed_esmtCntr.Text = aRtn[1];
    }
}

/********************************************************************************
* 기      능   : 조회 결과 그리드 더블클릭.
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	btn_esmtDoc_OnClick(btn_esmtDoc);
}

/********************************************************************************
* 기      능   : 조회 그리드 정렬
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	// 그리드 정렬 처리 
	gfn_sortGridFS(obj,nCell);
}

/********************************************************************************
* 기      능   : 조회 화면 금액 숨김 처리
********************************************************************************/
function fn_btnControll()
{	
	grd_list.SetCellProp("Body", grd_list.GetBindCellIndex("Body","ESMT_TAMT"), "Display", iif( G_DPT1 == "CD" , "Number", "Null" ));
	grd_list.SetCellProp("Body", grd_list.GetBindCellIndex("Body","NGTT_TAMT"), "Display", iif( G_DPT1 == "CD" , "Number", "Null" ));
	grd_list.SetCellProp("Body", grd_list.GetBindCellIndex("Body","CRES_TAMT"), "Display", iif( G_DPT1 == "CD" , "Number", "Null" ));
	grd_list.SetCellProp("Body", grd_list.GetBindCellIndex("Body","CRNG_TAMT"), "Display", iif( G_DPT1 == "CD" , "Number", "Null" ));
}
