#include "LIB::commons.js";           // 공통 스크립트
#include "LIB::fs_common.js";         // 물류서비스 공통 스크립트

//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
    gfn_initForm(true);             // Form 로드 이벤트 맨 첫줄에 기술
    fn_init_form();             // 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
    return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
    var codeArray = ["A03:ds_combo_a03"];
    // 공용코드 콤보 데이터 셑을 가져온다.
    gn_fsComboDataSet(codeArray);

    ed_escsNumb.Text = escsNumb;
	ed_escsNtim.Text = escsNtim;
    // 전달 파라미터 값 셑팅
    ds_cond.SetColumn(0, "ESCS_NUMB", escsNumb);
    ds_cond.SetColumn(0, "ESCS_NTIM", escsNtim);

    // 견적원가 드롭다운 그리드 선언
    gfn_InitDrillDown(ds_zfst0201_list, grd_master, "ITEM_DIYN=='M'", "FLAG", "LEVEL", "PESCS_GROP", "PESCS_UPGR", "ESCE_NAME", "CHK");

    // 3단 콤보 데이터를 조회한다.
    tit_clearActionInfo();
    tit_addSearchActionInfo("fs01:FS0101001B_S4");
    tit_callService("","","ds_cond=ds_cond","ds_combo_c01=ds_combo_c01","","fn_afterC01Query","true","true","false");
}

/********************************************************************************
* 기      능   : FS0101001B_S4 Callback
********************************************************************************/
function fn_afterC01Query(errCode, errMsg) {
    if(ds_error.rowcount() > 0) {
        gfn_showMsg("CE00001");
    } else {
		ds_combo_c01.InsertRow(0);
		ds_combo_c01.SetColumn(0, "CODE", "");
		ds_combo_c01.SetColumn(0, "CODE_NAME", "== 선택 ==");
		cb_cs01_code.Index = 0;
		cb_cs02_code.Index = 0;
		cb_cs03_code.Index = 0; 
	    fn_query();
    }
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
    // 조회 스크립트 

    ds_cond.SetColumn(0, "ESCS_ITEM", "");
    if( cb_cs01_code.Value != "" && cb_cs02_code.Value != "" && cb_cs03_code.Value != "" ) {
        ds_cond.SetColumn(0, "ESCS_ITEM", cb_cs03_code.Value);
    } else if(cb_cs01_code.Value != "" && cb_cs02_code.Value != "") {
        ds_cond.SetColumn(0, "ESCS_ITEM", cb_cs02_code.Value);
    } else if(cb_cs01_code.Value != "") {
        ds_cond.SetColumn(0, "ESCS_ITEM", cb_cs01_code.Value);
    } else {
        if( cb_cs01_code.Value != "" || cb_cs02_code.Value != "" || cb_cs03_code.Value != "" ) {
			gfn_openMsgWin("예산항목 코드 검색조건을 단계별로 선택해 주세요", "", "A", "");
			return;
        }
    }

    // ITEM_DIVS
    if( ds_cond.GetColumn(0, "ESCS_ITEM") != "" || ds_cond.GetColumn(0, "WORD_REFR") != "" ) {
        ds_cond.SetColumn(0, "ITEM_DIVS", "Y");
    } else {
        ds_cond.SetColumn(0, "ITEM_DIVS", "N");
    }

    tit_clearActionInfo();
    tit_addSearchActionInfo("fs02:FS0202002B_S1");
    tit_callService(
                    ""
                   ,""
                   ,"ds_cond=ds_cond"
                   ,"ds_zfst0201_list=ds_zfst0201_list"
                   ,""
                   ,"fn_afterQuery"
                   ,"true"
                   ,"true"
                   ,"false"
                   );
}

/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
    if(ds_error.rowcount() > 0) {
        gfn_showMsg("CE00001");
    } else {
        //gfn_openMsgWin("견적원가번호를 입력하십시요", "", "A", "");
        if( ds_zfst0201_master.GetRowCount() == 0 ) {
			var stringFilter = "ITEM_DIYN='D'";
			ds_zfst0201_detail.FilterExpr = stringFilter;
			ds_zfst0201_detail.RecalcFilter();
        } else {
			var stringFilter = "ITEM_DIYN=='D' && PESCS_UPGR == ds_zfst0201_master.GetColumn(ds_zfst0201_master.GetRowPos(), 'PESCS_GROP')";
			ds_zfst0201_detail.FilterExpr = stringFilter;
			ds_zfst0201_detail.RecalcFilter();
        }
    }
}

/********************************************************************************
* 기      능   : 복사
********************************************************************************/
function fn_copy() 
{
	var nCopyCount = ds_zfst0201_list.CaseCount("CHK=='1'");
    if( nCopyCount == 0 ) {
		gfn_openMsgWin("선택된 품목이 없습니다.", "", "A", "");
		return;
    }

	if( gfn_showConfirm("견적 항목을 복사 하시겠습니까?") ) {
		var iRow = -1;
		var cRow = -1;
		while( true )
		{
			iRow = ds_zfst0201_list.SearchRow("CHK!=''", iRow+1);
			if (iRow < 0) break;
			cRow = Parent.ds_zfst0201_rtn.AppendRow();
			Parent.ds_zfst0201_rtn.CopyRow(cRow, ds_zfst0201_list, iRow);
		}

		gfn_openMsgWin("총 "+nCopyCount+"건이 복사 되었습니다.", "", "A", ""); 

		close();
	}
}

/********************************************************************************
* 기      능   : 상위 로우를 리턴한다.
********************************************************************************/
function fn_getUpRow(pescsUpgr) {
    return ds_zfst0201_list.FindRow("PESCS_GROP", pescsUpgr);
}

/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
    close();
}

/********************************************************************************
* 기      능   : 접기 와 펼치기
********************************************************************************/
function grd_master_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var columnID = obj.GetCellProp("body",nCell,"colid");
	var value = obj.GetCellValue(nRow, nCell);
	switch ( columnID ) {
	case "CHK":
	    gfn_DrillDownCheckBox(obj, nRow, "0");
		break;
	case "FLAG":
		ds_zfst0201_master.SetColumn(nRow, columnID, iif( value == '1' , '0', '1' ));
		ds_zfst0201_master.RecalcFilter();
		break;
	}
}

/********************************************************************************
* 기      능   : 상세조회
********************************************************************************/
function grd_master_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	ds_zfst0201_detail.RecalcFilter();
}

/********************************************************************************
* 기      능   : 예산 code1 항목 데이터 셑 필터 적용
********************************************************************************/
var _cs01_code = false;
function cb_cs01_code_OnFocus(obj)
{
    if(!_cs01_code) {
        var stringFilter = "CODE='' || LEVEL='1'";
        ds_combo_cf01.Filter(stringFilter);
        _cs01_code = true;
    }
}

/********************************************************************************
* 기      능   : 예산 code1 항목 선텍시
********************************************************************************/
function cb_cs01_code_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var stringFilter2 = "CODE='' || LEVEL='2'";
    if( strCode != "" ) {
        stringFilter2 = "CODE='' || (LEVEL='2' && IndexOf(CODE, '"+strCode+"') > -1) ";
        ds_combo_cf02.Filter(stringFilter2);
    }

    if( cb_cs02_code.Value != "" ) {
		cb_cs02_code.Index =0;
    }
    if( cb_cs03_code.Value != "" ) {
		cb_cs03_code.Index =0;
    }
}

/********************************************************************************
* 기      능   : 예산 code2목 데이터 셑 필터 적용
********************************************************************************/
var _cs02_code = false;
function cb_cs02_code_OnFocus(obj)
{
    if(!_cs02_code) {
        var stringFilter = "CODE='' || LEVEL='2'";
        ds_combo_cf02.Filter(stringFilter);
        _cs02_code = true;
    }
}

/********************************************************************************
* 기      능   : 예산 code2 항목 선텍시
********************************************************************************/
function cb_cs02_code_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var stringFilter3 = "CODE='' || LEVEL='3'";
    if( strCode != "" ) {
        stringFilter3 = "CODE='' || (LEVEL='3' && IndexOf(CODE, '"+strCode+"') > -1) ";
        ds_combo_cf03.Filter(stringFilter3);
    }

    if( cb_cs03_code.Value != "" ) {
		cb_cs03_code.Index =0;
    }
}

/********************************************************************************
* 기      능   : 예산 code3목 데이터 셑 필터 적용
********************************************************************************/
var _cs03_code = false;
function cb_cs03_code_OnFocus(obj)
{
    if(!_cs03_code) {
        var stringFilter = "CODE='' || LEVEL='3'";
        ds_combo_cf03.Filter(stringFilter);
        _cs03_code = true;
    }
}

/********************************************************************************
* 기      능   : 디테일 체크 시
********************************************************************************/
function grd_detail_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var columnID = obj.GetCellProp("body",nCell,"colid");
	var value = obj.GetCellValue(nRow, nCell);
	switch ( columnID ) {
	case "CHK":
        //var nRowNF = ds_zfst0201_detail.GetRowIndexNF(nRow);
        var escsUpgr = ds_zfst0201_detail.GetColumn(nRow, "PESCS_UPGR");
        var chkCnt = ds_zfst0201_detail.CaseCount("CHK='1'");
        var findRow = ds_zfst0201_list.FindRow("PESCS_GROP", escsUpgr);
        if( value == "" ) {
            chkCnt = chkCnt + 1;
        } if( value == "0" ) {
            chkCnt = chkCnt + 1;
        } else if( value == "1" ) {
            chkCnt = chkCnt - 1;
        }

        if( chkCnt == 0 ) {
            ds_zfst0201_list.SetColumn(findRow, "CHK", "");
        } else {
            ds_zfst0201_list.SetColumn(findRow, "CHK", "1");
        }
		break;
	}
}
