#include "LIB::commons.js";           // 공통 스크립트
#include "LIB::fs_common.js";         // 물류서비스 공통 스크립트

//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
    gfn_initForm(true);             // Form 로드 이벤트 맨 첫줄에 기술
    fn_init_form();             // 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
    return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
    // 공용코드 데이터 코드 타입: CODE_GROP:DATASETID -> A02:ds_combo_a02, A03:ds_combo_a03
    var codeArray = ["A03:ds_combo_a03::EXTN1='_%'"];
    // 공용코드 콤보 데이터 셑을 가져온다.
    gn_fsComboDataSet(codeArray);

    // 검색일 초기값 정의    
    var month = gfn_getCurrMonth();
    var agoMonth = gfn_getBeforeMonth( month, 1);

    ca_esmtDateFrom.value = agoMonth+"01";
    ca_esmtDateTo.value = gfn_getCurrDate();

    fn_query();
}

/********************************************************************************
* 기      능   : 수주생성현황 조회
********************************************************************************/
function fn_query() {

    // 견적원가작성일 시작일자와 종료일자를 기본값을 세팅한다.
    if( !(ca_esmtDateFrom.value == "" && ca_esmtDateTo.value == "") ) {
		if ( !gfn_checkFromToObj(ca_esmtDateFrom, ca_esmtDateTo, true, st_esmtDate.value, 0, true) ) {
			return;
		}
		if ( !gfn_checkFromToObj(ca_esmtDateFrom, ca_esmtDateTo, true, st_esmtDate.value) ) {
			return;
		}
    }

    gfn_clearGridSortMark(grd_list);

    // 조회 스크립트
    tit_clearActionInfo();
    tit_addSearchActionInfo("fs03:FS0301001A_S1");
    tit_callService(
                    ""
                   ,""
                   ,"ds_cond=ds_cond"
                   ,"ds_zfst0202_list=ds_zfst0202_list"
                   ,""
                   ,"fn_afterQuery"
                   ,"true"
                   ,"true"
                   ,"false"
                   );
}

/********************************************************************************
* 기      능   : 수주생성현황 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
    if(ds_error < 0) {
        gfn_showMsg("CE00001");
    } else {
        gfn_showMsg("CI00002", ds_zfst0202_list.rowCount() + "");
    }
}

/********************************************************************************
* 기      능   : 닫기
********************************************************************************/
function fn_close() {
    close();
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
        // 그리드 정렬 처리 
		gfn_multiSortGrid(obj, false, 1);
	} else {
        // 그리드 정렬 처리 
	    gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 견적원가 조회 팝업을 후출한다.
********************************************************************************/
function btn_budget_num_OnClick(obj)
{
    var arg="";
    var arr=null;
    arr = gfn_openPopup("FS0201001B", true, arg);
    if( length(arr) > 0 ) {
		ds_cond.SetColumn(0, "ESCS_NUMB", arr[0]);
    }
}

/********************************************************************************
* 기      능   : 고객정보 팝업을 호출한다.
********************************************************************************/
function btn_cusnName_OnClick(obj)
{
	var arg="";
	var arr=null;
    arr = gfn_openPopup("FS_COM0201", true, arg);
    ds_cond.fireevent = false;
    if( length(arr) > 0 )  {
        if( arr[0] != "" ) {
			ds_cond.SetColumn(0, "KUNNR", arr[0]);
			ds_cond.SetColumn(0, "KUNNR_NAME", arr[1]);
        } else {
			ds_cond.SetColumn(0, "KUNNR", "");
			ds_cond.SetColumn(0, "KUNNR_NAME", "");
        }
    }
    ds_cond.fireevent = true;
}

/********************************************************************************
* 기      능   : 국가코드 팝업을 호출한다.
********************************************************************************/
function btn_cntrCode_OnClick(obj)
{
	var arg="";
	var arr = null;
    arr = gfn_openPopup("FS_COM0901", true, arg);
    ds_cond.fireevent = false;
    if( length(arr) > 0 )  {
        ds_cond.SetColumn(0, "ESMT_CNTR", arr[0]);
        ds_cond.SetColumn(0, "ESMT_CNTR_NAME", arr[1]);
    }
    ds_cond.fireevent = true;
}

/********************************************************************************
* 기      능   : 수주생성 화면을 호출한다.
********************************************************************************/
function btn_order_create_OnClick(obj)
{
    var chkCnt = ds_zfst0202_list.CaseCount("CHK='1'");
    ds_escs_numb.ClearData();
    ds_esmt_numb.ClearData();
    if(chkCnt ==0) {
        gfn_openMsgWin("한개 이상의 견적서번호를 선택하세요.", "", "A", "");
        return;
    }
 	if( gfn_showConfirm("선택하신 견적서번호로 수주번호를 생성하시겠습니까?") ) {
 	    ds_zfst0202_list.Filter("CHK='1'");
 	    // 다중 견적서에 대한 수주번호를 생성은 같은 고객번호에 대해서만 수주 번호를 생성할 수 있다. IN_ESMT_NUMB quote(escsGrop)
		var esmtNumb = ds_zfst0202_list.GetColumn(0, "ESMT_NUMB");
		var kunnr = ds_zfst0202_list.GetColumn(0, "KUNNR");
		var ordrType = ds_zfst0202_list.GetColumn(0, "ORDR_TYPE");
		var waerk = ds_zfst0202_list.GetColumn(0, "WAERK");
		for(var i=0; i < ds_zfst0202_list.GetRowCount(); i++) {
			if( esmtNumb != ds_zfst0202_list.GetColumn(i, "ESMT_NUMB") ) {
                ds_zfst0202_list.UnFilter();
				gfn_openMsgWin("동일한 견적원가 번호로 다중 견적서는 수주번호를 생성할 수 없습니다.\n선택하신 견적서번호를 확인 하세요", "", "A", "");
				return;
			}

			if( kunnr != ds_zfst0202_list.GetColumn(i, "KUNNR") ) {
                ds_zfst0202_list.UnFilter();
				gfn_openMsgWin("고객번호가 다른 다중 견적서는 수주번호를 생성할 수 없습니다.\n선택하신 견적서번호를 확인 하세요", "", "A", "");
				return;
			}

			if( ordrType != ds_zfst0202_list.GetColumn(i, "ORDR_TYPE") ) {
                ds_zfst0202_list.UnFilter();
				gfn_openMsgWin("오더타입이 다른 다중 견적서는 수주번호를 생성할 수 없습니다.\n선택하신 견적서번호를 확인 하세요", "", "A", "");
				return;
			}

            // 견적서의 국가 화폐 단위가 다르면 견적서를 통합하여 수주번호를 생성할 수 없다.
			if( waerk != ds_zfst0202_list.GetColumn(i, "WAERK") ) {
                ds_zfst0202_list.UnFilter();
				gfn_openMsgWin("통화단위가 다른 다중 견적서는 수주번호를 생성할 수 없습니다.\n선택하신 견적서번호를 확인 하세요", "", "A", "");
				return;
			}

            // 견적원가 키 리스트
            ds_escs_numb.AddRow();
			ds_escs_numb.SetColumn(i, "ESCS_NUMB", ds_zfst0202_list.GetColumn(i, "ESCS_NUMB"));
			ds_escs_numb.SetColumn(i, "ESCS_NTIM", ds_zfst0202_list.GetColumn(i, "ESCS_NTIM"));
			ds_escs_numb.SetColumn(i, "PK_ESCS_NUMB", ds_zfst0202_list.GetColumn(i, "ESCS_NUMB")+ds_zfst0202_list.GetColumn(i, "ESCS_NTIM"));

            // 견적서 키 리스트
            ds_esmt_numb.AddRow();
			ds_esmt_numb.SetColumn(i, "ESMT_NUMB", ds_zfst0202_list.GetColumn(i, "ESMT_NUMB"));
		}

		// 수주번호 생성 -> ZFST0301 초기 데이터 입력 ZFST0304 최초생성 > ZFST0200 상태값 변경 수행
		// 선택된 견적서 번호를 갖고 수주번호를 생성하고 견적원가 테이블에 수주 번호를 적용한다.
		// 만약 견적서 금액과 견적원가 금액이 다른 경우 처리 확인해 볼 필요 있음
		tit_clearActionInfo();
		tit_addSearchActionInfo("fs03:FS0301001A_S2", true);
		tit_addSingleActionInfo("fs03:FS0301001A_I1");  // 시행예산 마스터 생성
		tit_addSingleActionInfo("fs03:FS0301001A_I2");  // 시행예산 품목생성                             
		tit_addSingleActionInfo("fs03:FS0301001A_I3");  // 수주마스터 생성
		//tit_addSingleActionInfo("fs03:FS0301001A_I4");  // 수주마스터 이력 생성
		tit_addSingleActionInfo("fs03:FS0301001A_U1");  // 견적원가 상태코드 처리 -- OCNT_PDYN
		tit_addSingleActionInfo("fs03:FS0301001A_U2");  // 견적원가 수주번호 처리
		tit_addSingleActionInfo("fs03:FS0301001A_I5");  // 월별청구수금계획 입력
		tit_callService(
						""
					   ,""
					   ,"ds_cond=ds_cond ds_escs_numb=ds_escs_numb ds_esmt_numb=ds_esmt_numb"
					   ,"ds_zfst0301=ds_zfst0301"
					   ,""
					   ,"fn_createOcntNumb"
					   ,"true"
					   ,"true"
					   ,"false"
					   );
    }

    // 필터상태로 원복한다.
    ds_zfst0202_list.UnFilter();
}

/********************************************************************************
* 기      능   : 수주생성 callBack
********************************************************************************/
function fn_createOcntNumb(errCode, errMsg)
{
    if (errCode != 0) {
		gfn_showMsg("CE00001");
    } else {
       	if( gfn_showConfirm("수주번호가 생성 되었습니다. 수주번호 등록 화면으로 이동하시겠습니까?") ) {
       	    // 아닌경우 재조회 수행
            fn_query();
			var arg = "";
			arg = arg +" ocntNumb="+quote(ds_zfst0301.GetColumn(0, "OCNT_NUMB"));
			arg = arg +" rfctSeqn="+quote("0");  // RFCT_SEQN
			arg = arg +" ocntStat="+quote("230");  // OCNT_STAT
			gfn_openWin("FS0301003A", arg);
       	} else {
       	    // 아닌경우 재조회 수행
            fn_query();
       	}
    }
}
