// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	gfn_clearMsgBar();

	ds_list.ClearData();

	if ( gfn_isNull(ed_hogin_fm, true) ) {
		gfn_showMsg("CW00001",st_hno.value);
		return;
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	gfn_clearGridSortMark(grd_list2);
	ds_list.row = -1;

	var hnogbn1 = "";
	var hnogbn2 = "";
	var itngbn1 = "";
	var itngbn2 = "";

	if (length(ed_hogin_fm.value) > 0 && length(ed_hogin_fm.value) < 6) {
		gfn_showMsg("CW00092");
		ed_hogin_fm.SetFocus();
		return;
	}

	if (length(ed_hogin_to.value) > 0 && length(ed_hogin_to.value) < 6) {
		gfn_showMsg("CW00092");
		ed_hogin_to.SetFocus();
		return;
	}

	if (length(ed_matnr_fm.value) > 0 && length(ed_matnr_fm.value) < 6) {
		gfn_showMsg("CW00096");
		ed_matnr_fm.SetFocus();
		return;
	}

	if (length(ed_matnr_to.value) > 0 && length(ed_matnr_to.value) < 6) {
		gfn_showMsg("CW00096");
		ed_matnr_to.SetFocus();
		return;
	}

	hnogbn1 = ed_hogin_fm.value;
	if (length(ed_hogin_fm.value)=9 && ed_hogin_to.value = "")
	{
		hnogbn2 = hnogbn1;
	}
	else
	{
		hnogbn2 = gfn_fillChar(ed_hogin_to.value,9,"Z");
	}
	itngbn1 = ed_matnr_fm.value;
	itngbn2 = gfn_fillChar(ed_matnr_to.value,18,"Z");

	t_hnogbn1 = ed_hogin_fm.value;
	t_hnogbn2 = ed_hogin_to.value;
	t_itngbn1 = ed_matnr_fm.value;
	t_itngbn2 = ed_matnr_to.value;

	ds_cond.SetColumn(0, "HOGIN_FROM", hnogbn1);
	ds_cond.SetColumn(0, "HOGIN_TO", hnogbn2);
	ds_cond.SetColumn(0, "MATNR_FROM", itngbn1);
	ds_cond.SetColumn(0, "MATNR_TO", itngbn2);

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm01:MM0102001A_S1", null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 세부내역 조회 
********************************************************************************/
function fn_query2(CHNDA, HOGIN, MATNR) {

	ds_cond2.setColumn(0, "HOGIN", HOGIN);
	ds_cond2.setColumn(0, "MATNR", MATNR);

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm01:MM0102001A_S2", null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list3=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);
}
