//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	ds_cond.SetColumn(0, "HOGIN_FROM", t_hnogbn1);
	ds_cond.SetColumn(0, "HOGIN_TO", t_hnogbn2);
	ds_cond.SetColumn(0, "MATNR_FROM", t_itngbn1);
	ds_cond.SetColumn(0, "MATNR_TO", t_itngbn2);

	ds_list2.ClearData();

	gfn_showMsg("CI00002",ds_list.RowCount()+"");
}

function fn_afterQuery2(errCode, errMsg) {
	ds_list2.ClearData();

	for(var i = 0; i <= 40; i++) {
		if (i == 0) {
			var str = "";
			str = "호기번호 : " + ds_list.GetColumn(c_row, "HOGIN") + ", " +
				  "공사명 : " + ds_list.GetColumn(c_row, "POST1") + ", " +
				  "제품사양 : " + ds_list.GetColumn(c_row, "ZSPEC2") + ", " +
				  "도면번호 : " + ds_list.GetColumn(c_row, "DZEINR") + ", " +
				  "자재번호 : " + ds_list.GetColumn(c_row, "MATNR") + ", " +
				  "자재내역 : " + ds_list.GetColumn(c_row, "MAKTX") + "";

			ds_list2.addRow();
			ds_list2.setColumn(0, "TEXT", str);
		} else {		
			if(length(trim(ds_list3.GetColumn(i-1, "TEXT"))) > 0) {
				var row = ds_list2.addRow();
				ds_list2.setColumn(i, "TEXT", ds_list3.GetColumn(i-1, "TEXT"));
			} else {
				break;
			}
		}
	}

	ds_list2.row = 0;
//	ufn_test();
}

