//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	//타이머 테스트 
	//settimer(1,600000);

	// 사용그룹 처리
	user_grp = gfn_getUserGroup();
	if(user_grp == '10') 
	{
		ds_cond.SetColumn(0,"GBN","10");
	} 
	else if(user_grp == '30' || user_grp == '40') 
	{
		ds_cond.SetColumn(0,"GBN","");
	}

    // 코드 DataSet 가져오기
    // ds_part 하드 코딩 --->>공통코드로 변환 'PARTOPTION'  20170306 이하림 
    // 동적 SQL 로 변경 
    // ds_part 하드코딩 버전 백업 --->> ds_part_back

    var codeArr = ["10", "PARTOPTION"];
    var dsObjArr = [ds_ekgrp_cd, ds_part];
    var addTypeArr = [G_CODE_ALL,G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

	// 화면 초기화
	cb_part.Index = 0;
	csh_cb_boxck.Index = 0;
	ed_hogin.SetFocus();

	// 버튼 비활성화
	ufn_enableBtn("excel",  false);
	ufn_enableBtn("out",  false);

	// 메세지
	gfn_showMsg("CI00007");	
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	// 해외 부품판매 법인 사용자 체크 후 일부 항목 숨김 처리 
	if(gfn_getAuthGroup("ZCS81")) fn_hideValue();

	csh_cb_ekgrp.index = 0 ;
}

/********************************************************************************
* 기      능   : 해외 부품판매 법인 사용자 항목 숨김 처리
********************************************************************************/
function fn_hideValue() {

	//m/o번호 숨김
	gfn_hideGridColumn(grd_list,7);
	gfn_hideGridColumn(grd_list,8);
	gfn_hideGridColumn(grd_list,10);
	gfn_hideGridColumn(grd_list,11);
	gfn_hideGridColumn(grd_list,17);
	gfn_hideGridColumn(grd_list,18);
	gfn_hideGridColumn(grd_list,19);
	gfn_hideGridColumn(grd_list,24);
	gfn_hideGridColumn(grd_list,25);
	gfn_hideGridColumn(grd_list,26);
	gfn_hideGridColumn(grd_list,27);
	gfn_hideGridColumn(grd_list,28);
	gfn_hideGridColumn(moveTab00.tab1.grd_list2,5);
	gfn_hideGridColumn(moveTab00.tab2.grd_list3,6);

	for(iCnt=0;iCnt < Components.count();iCnt++)
	{
		if(indexOf(Components[iCnt].ID, "csh_") >= 0 )
		{
			Components[iCnt].visible = false;
		}
	}
}

