/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
	//trace(ds_cond.SaveXML());

	// 호기번호 필수 검사
	if (length(Trim(ed_hogin.Text)) < 6) 
	{
		gfn_showMsg("CW00092");
		ed_hogin.SetFocus();
		return;
	}
	//외주설계직원 구매그룹, 협력업체 조회 권한 부여 2016.04.21 조영욱
	//조회 권한 추가 2018.08.07, 2019.02.11(7003507), 2019.02.15(7003518), 2021.02.09(7004354)
	if(g_user_id = "7001037" || g_user_id = "7001041" || g_user_id = "7001132" ||
	   g_user_id = "7001131" || g_user_id = "7001130" || g_user_id = "7001395" ||
	   g_user_id = "7001962" || g_user_id = "7002427" || g_user_id = "7002867" ||
	   g_user_id = "7003177" || g_user_id = "7002868" || g_user_id = "7003176" ||
	   g_user_id = "7003178" || g_user_id = "7003286" || g_user_id = "7003411" ||
	   g_user_id = "3019101" || g_user_id = "7003507" || g_user_id = "7003518" ||
	   g_user_id = "7004354" || g_user_id = "A001440")
    {	
		ds_cond.SetColumn(0,"DPT","X");
	} else {
		ds_cond.SetColumn(0,"DPT",G_DPT1);
	}

	posid = Trim(ed_hogin.Text);	
	if (posid.substr(0,2) == "QM")
	{
		ds_cond.SetColumn(0,"POSID",posid.substr(2,6));
	}
	else if (posid.substr(0,1) == "Q")
	{
		ds_cond.SetColumn(0,"POSID",posid.substr(1,6));
	}
	// N 현장은 호기번호 전체 조회 되도록 셋팅 200525 yj 임홍조
	else if (posid.substr(6,1) == "N")
	{
		ds_cond.SetColumn(0,"POSID",posid.substr(0,11));
	}
	else
	{
		ds_cond.SetColumn(0,"POSID",posid.substr(0,6));
	}

	// 특정edit에 숫자나 영문이 들어가지 않으면 조회 불가 200918 yj
	if (chk_edcode(trim(ed_hogin.Text)) == false) {
		alert("호기번호에는 영문과 숫자만 입력 가능합니다.");
		return false;
	}
	else if (chk_edcode(trim(ed_block.Text)) == false) {
		alert("BLOCK번호에는 영문과 숫자만 입력 가능합니다.");
		return false;
	}
	else if (chk_edcode(trim(ed_matnr.Text)) == false) {
		alert("자재번호에는 영문과 숫자만 입력 가능합니다.");
		return false;
	}

	gfn_clearMsgBar();

	ds_list.ClearData();

	// 호기번호 필수 검사 200715 yj 추가
	if (length(Trim(ed_hogin.Text)) < 6) 
	{
		gfn_showMsg("CW00092");
		ed_hogin.SetFocus();
		return;
	}

	// sort mark 삭제 	
	ds_list.row = -1;
	tit_clearActionInfo();

	if ( length(trim(ds_cond.GetColumn(0,"HOGIN"))) < 6 )
	{
		gfn_showMsg("CW10025");
		ed_hogin.SetFocus();
		return;
	}

	tit_addSearchActionInfo("mm01:MM0102002A_S4"); 

	tit_addSearchActionInfo("mm01:MM0102002A_S1", null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_head=ds_head"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 세부내역 조회 
********************************************************************************/
function fn_query2() 
{
	if ( length(Trim(ds_cond2.GetColumn("0","HOGIN"))) < 6 )
	{
		return;
	}

	//  텝 컨트롤
	var actStr = "";
	var dsStr  = "";

	if ( moveTab00.TabIndex == 0 )
	{
		actStr = "mm01:MM0102002A_S2"; 
		dsStr  = "ds_list2=ds_list";
	}
	else
	{
		actStr = "mm01:MM0102002A_S3"; 
		dsStr  = "ds_list3=ds_list";
	}

	// 서비스 실행 
	tit_clearActionInfo();

	tit_addSearchActionInfo(actStr, null, true);

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond2"
        , dsStr
        , ""
        , ""
        , true);
}
