/********************************************************************************
* 기      능   : 리스트의 폰트 색깔 처리 
********************************************************************************/
function ufn_setColor(obj,cPRCTYP)
{
	cValue = decode(cPRCTYP,'추가','blue','삭제','red','변경','green','default');
	// 선택된 로우의 Font Color
	obj.SelColor = cValue;
	// Grid의 Font Color
	return cValue;
}

