* 기      능   : 리스트 더블 클릭(품목표 상세내역)
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	// 품목표 조회 팝업
	// if (nCell == 1 || nCell==5)
	// {
		// var arg = "HOGIN=" + quote(obj.GetCellValue(nRow,1))
		        // + " MATNR=" + quote(obj.GetCellValue(nRow,5));

		// gfn_openPopup("MM0102002B", true, arg, 420, 529);
	// }

	// 품목 기타정보 (발주업체) 조회 팝업
	if (nCell==28)
	{ 
		var arg = "fa_lifnr=" + quote(ds_list.GetColumn(nRow,"LIFNR"));

		gfn_openPopup("MM0102002D", true, arg, 420, 529);
	} 
	//도면번호 조회 팝업
	else if (nCell==9 || nCell==16)
	{
		var arg = "fa_dzeinr=" + quote(ds_list.GetColumn(ds_list.rowpos,"dzeinr"));

		gfn_openPopup("MM0102002F", true, arg, 420, 529);

	} else {
		var arg = "fa_posid=" + quote(ds_list.GetColumn(nRow,"HOGIN")) + " fa_zsite_nm=" + quote(ds_head.GetColumn(0,"ZSITE_NM"));

		gfn_openPopup("MM0102002E", true, arg, 420, 529);
	}

}
