/********************************************************************************
* 기      능   :  텝 변경 
********************************************************************************/
function moveTab00_OnChanged(obj,nOldIndex,nNewindex)
{
	if( obj.TabIndex == 0) 
	{
		ufn_enableBtn("excel", true);

		if ( ds_list2.RowCount() == 0 )
		{
			fn_query2();
		}
	}
	else
	{
		ufn_enableBtn("excel", false);

		if ( ds_list3.RowCount() == 0 )
		{
			fn_query2();
		}
	}	
}

