/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{

	gfn_clearMsgBar();
	ds_list.ClearData();

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);

	ds_list.row = -1;
	tit_clearActionInfo();

	tit_addSearchActionInfo("mm01:MM0103001A_S1");

	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 저 장
********************************************************************************/
function fn_save() 
{
	//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	if (!gfn_showConfirm("CI00008")) {
		return;
	}

	gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	if (ds_list2.rowcount < 1) {
		gfn_showAlert("CW00180","체크표시");
		return;
	}

	for (var i = 0; i < ds_list2.GetRowCount(); i++) {

		if(ds_list2.GetColumn(i,"ZGUBN") == "" || ds_list2.GetColumn(i,"ZGUBN") = null)  {
			gfn_showAlert("CW00022","호기정보 데이터");
			return;
		}
		if(ds_list2.GetColumn(i,"POST1") == "" || ds_list2.GetColumn(i,"POST1") = null) {
			gfn_showAlert("CW00022","호기정보 데이터");
			return;
		}
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm01:MM0103001A_I1");	

	// 저장 처리 
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave"
		, true);	
}

/********************************************************************************
* 기      능   : 삭 제
********************************************************************************/
function fn_delete() 
{
	//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	if (!gfn_showConfirm("CI00015","선택한 호기를")) {
		return;
	}

	ds_list2.ClearData();

	gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	if (ds_list2.rowcount < 1) {
		gfn_showAlert("CW00180","체크표시");
		return;
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm01:MM0103001A_D1");	

	// 저장 처리 
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterDelete"
		, true);	
}
