/********************************************************************************
* 기      능   : 엑셀업로드   
********************************************************************************/
function btn_upload_OnClick(obj)
{
	grd_list.Format = "Upload";

	Create("FileDialog", "_fileDialog_", "");
	var dialogObj = object("_fileDialog_");

	dialogObj.Type = "Open";
	dialogObj.FileName = "";
	dialogObj.Filter = "Microsoft Excel File(*.xls)|*.xls|";
	dialogObj.open();

	if (length(dialogObj.FileName) == 0) {
		Destroy( "_fileDialog_" );
		return;
	}

	var sFileName = dialogObj.FilePath + "\\" + dialogObj.FileName;

	ext_ExcelImportByIndex(sFileName,null,"ds_excel",0,1);

	grd_list.BindDataset = "";

	ufn_ExcelToList();

	grd_list.BindDataset = "ds_list";

	Destroy( "_fileDialog_" );  // 불러오기후 dialog 제거	


}

function ufn_ExcelToList()
{
	ds_list.ClearData();
	grd_list.SetCellProp("head", 0, "Text", "1");

	for(i=1; i < ds_excel.rowcount ; i++) {
		//호기번호 자리수 체크
		if(length(ds_excel.GetColumn(i,0)) <> 11) {
			gfn_showAlert("CW00022","호기번호의 값은 11자리이어야 합니다. ( " + i + " ) 행의 호기번호" );
			ds_list.ClearData();
			return;
		}

		ds_list.AddRow();
		ds_list.SetColumn(i-1,"CHECK","1");
		ds_list.SetColumn(i-1,"WOKNUM", ds_excel.GetColumn(i,0));
		ds_list.SetColumn(i-1,"UPMATKL", ds_excel.GetColumn(i,1));
		ds_list.SetColumn(i-1,"MATKL", ds_excel.GetColumn(i,2));
		ds_list.SetColumn(i-1,"MAKTX", ds_excel.GetColumn(i,3));
		ds_list.SetColumn(i-1,"DZEINR", ds_excel.GetColumn(i,4));
		ds_list.SetColumn(i-1,"MENGE", ds_excel.GetColumn(i,5));
		ds_list.SetColumn(i-1,"MATNR", ds_excel.GetColumn(i,6));
		ds_list.SetColumn(i-1,"SPEC", ds_excel.GetColumn(i,7));
		ds_list.SetColumn(i-1,"REMARK", ds_excel.GetColumn(i,8));
		ds_list.SetColumn(i-1,"MEINS", ds_excel.GetColumn(i,9));
	}
}
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID <> "CHECK")
	{
		ds_list.fireevent = false;
		var colid = strColumnID;

		if(colid == "WOKNUM")
		{
			//자재정보 조회
			ds_cond2.SetColumn(0,"WOKNUM",varNewValue);
			ds_cond2.SetColumn(0,"CNT",nRow);

			//자재 정보조회
			tit_clearActionInfo();
			tit_addSearchActionInfo("mm01:MM0103001A_S2");

			tit_callService(
			""
			, ""
			, "ds_cond2=ds_cond2"
			, "ds_gubun=ds_gubun"
			, ""
			, "fn_refresh");
		}

		ds_list.fireevent = true;
	}
}

function fn_refresh()
{
	if(ds_gubun.GetRowCount() > 0)
	{
		ds_list.SetColumn(ds_gubun.GetColumn(0,"CNT"),"POST1",ds_gubun.GetColumn(0,"POST1"));
		ds_list.SetColumn(ds_gubun.GetColumn(0,"CNT"),"ZGUBN",ds_gubun.GetColumn(0,"ZGUBN"));
	}

	ds_list.fireevent = true;
}
