/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {

	ds_list.fireevent = false;
	ds_list.UnFilter();

	for (var i =0; i < ds_sum.rowcount; i++) {
		for (var j = 0 ; j < ds_list.rowcount; j++) {
			if (ds_list.GetColumn(j,"EBELN") == ds_sum.GetColumn(i,"EBELN")) {
				if (ds_sum.GetColumn(i,"CHECK") == "1")
				{
					ds_list.SetColumn(j,"CHECK", "1");
				} else {
					ds_list.SetColumn(j,"CHECK", "0");
				}
			}
		}
	}
	ds_list.fireevent = true;

	// Excel Export 
	ds_list.Filter("CHECK == 1");

	// Excel Export 
	/*2020.10.07 YJ
	  exportExcelEx로 export 할때 엑셀파일의 내용이 깨지는 경우(메모리때문으로 생각됨)가 있어
	  saveExcel 로 변경함
	gfn_exportExcel(grd_list,G_USER_ID); */

	gfn_saveToExcel(grd_list, , , , , G_USER_ID );
}

