//=========================================================================================
/********************************************************************************
* 기      능   :  거래명세서 생성
********************************************************************************/
function ufn_makeGeoRae() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 
	if ( gfn_isNull(cal_udate_fm, true) ) {
		gfn_showMsg("CW00001","납기일자(시작)");
		return;
	}
	if ( gfn_isNull(cal_udate_to, true) ) {
		gfn_showMsg("CW00001","납기일자(종료)");
		return;
	}

	if ( !gfn_checkDate( cal_udate_fm.value, cal_udate_to.value, true, cal_udate_to) ) {
		return;
	}

	if ( gfn_isNull(cb_lgort, true) ) {
		gfn_showMsg("CW00001","저장위치");
		return;
	}

	var arg = "fa_udate_fm=" + quote(cal_udate_fm.Value);
	arg += " fa_udate_to=" + quote( cal_udate_to.Value) ;
	arg += " fa_werks=" + quote( cb_werks.Value );
	arg += " fa_lgort=" + quote( cb_lgort.Value );
	arg += " fa_ekgrp=" + quote( cb_ekgrp.Value );
	arg += " fa_posid1=" + quote( ed_posid1.Value );
	arg += " fa_posid2=" + quote( ed_posid2.Value );
	arg += " fa_gubn=" + quote( cb_gubn.Value );

	gfn_copyDs(ds_sum, ds_sum_0, "CHECK=1");

	var fa_posid = "";
	var fa_extwg = "";
	var err[2];

	if (ds_sum_0.rowcount > 0) {
		fa_posid += "AND MM013B.POSID IN (";
		fa_extwg += "AND MM013B.EXTWG IN (";
		for (var i =0; i < ds_sum_0.rowcount ; i++){
			// 오발주 확인
			if (ds_sum_0.GetColumn(i,"BIGO") == "1") {
			    err[0] = grd_sum.GetCellProp("head",3,"text");
			    err[1] = ds_sum_0.GetColumn(i,"POSID");
				gfn_showAlert("CW00184", err);
				return;
			}		

			if (i == 0){
				fa_posid +=  "'"+ds_sum_0.GetColumn(i,"POSID")+"'";
				fa_extwg +=  "'"+ds_sum_0.GetColumn(i,"EXTWG")+"'";
			 } else if (i == ds_sum_0.rowcount-1) {
				 fa_posid +=  ",'"+ds_sum_0.GetColumn(i,"POSID")+"'";
				 fa_extwg +=  ",'"+ds_sum_0.GetColumn(i,"EXTWG")+"'";
			}
			else {
				fa_posid +=  ",'"+ds_sum_0.GetColumn(i,"POSID")+"'";
				fa_extwg +=  ",'"+ds_sum_0.GetColumn(i,"EXTWG")+"'";
			}
		}
		fa_posid += ")";
		fa_extwg += ")";
	}
	arg += " fa_posid=" + quote(fa_posid);
	arg += " fa_extwg=" + quote(fa_extwg);

   gfn_openWin("MM0301004A", arg);

}

//=========================================================================================
// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function grd_sum_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		//  전체 선택 해제
		gfn_selectAllGrid(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}


function grd_list_OnCellDBClick(obj,nRow,nCell){
	if (nCell == 3 || nCell==4){
		// 품목표 조회 팝업
		var arg = "HOGIN=" + quote(SubStr(obj.GetCellValue(nRow,3),0,9));
		arg += " MATNR=" + quote(obj.GetCellValue(nRow,7));
		arg += " MATKL=" + quote();

		gfn_openPopup("MM0102002B", true, arg, 420, 529);
	} else if (nCell == 7 || nCell == 8){
		//자재상세 조회 팝업
		gfn_openPopup("MM0901001A", true, "fa_matnr=" + quote(obj.GetCellValue(nRow,7)), 420, 529);
	}
}


// PO출력
function report_OnClick(obj)
{
	ds_sum.fireevent = false;
	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_sum_0, "CHECK=1");

	if (ds_sum_0.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		ds_sum.fireevent = true;
		return;
	}
	gfn_copyDs(ds_list, ds_list1);
	gfn_copyDs(ds_list1, ds_list2);

	ds_list2.ClearData();
	var row = 0;
	for (var i =0; i < ds_sum_0.rowcount ; i++){
		ds_list2.AddRow();
		row = ds_list2.RowCount() - 1 ;
		ds_list2.SetColumn(row,"EBELN",ds_sum_0.GetColumn(i,"EBELN"));
		ds_list2.SetColumn(row,"EXTWG",ds_sum_0.GetColumn(i,"EXTWG"));
		ds_list2.SetColumn(row,"BISMT",ds_sum_0.GetColumn(i,"BISMT"));
		ds_list2.SetColumn(row,"TXZ01",ds_sum_0.GetColumn(i,"TXZ01"));
		ds_list2.SetColumn(row,"POSID",ds_sum_0.GetColumn(i,"POSID"));
		ds_list2.SetColumn(row,"POST1",ds_sum_0.GetColumn(i,"POST1"));
		ds_list2.SetColumn(row,"NAME1",ds_sum_0.GetColumn(i,"NAME1"));
		ds_list2.SetColumn(row,"SDATE",ds_sum_0.GetColumn(i,"SDATE"));
		ds_list2.SetColumn(row,"RV_NAME",ds_list.GetColumn(0,"RV_NAME"));
		ds_list2.SetColumn(row,"R_EKGRP",ds_list.GetColumn(0,"R_EKGRP"));
		//ds_list2.SetColumn(row,"EINDT",ds_sum_0.GetColumn(i,"EINDT"));
		// ds_list2.SetColumn(row,"UDATE",ds_sum_0.GetColumn(i,"UDATE"));
		// ds_list2.SetColumn(row,"MEINS","");
		// ds_list2.SetColumn(row,"MENGE","");
		// ds_list2.SetColumn(row,"NETPR","");
		// ds_list2.SetColumn(row,"MATNR","");
		// ds_list2.SetColumn(row,"ZEINR","");

		ds_list1.filter("POSID == " + quote( ds_sum_0.GetColumn(i,"POSID"))+"&&"+
						"EXTWG == " + quote( ds_sum_0.GetColumn(i,"EXTWG")));

		ds_list2.AppendDataset("ds_list1");
	}
	ds_sum.fireevent = true;

	// UbiReport Call
	Dialog("MM02::MM0201002B.xml", "", -1, -1, "Resize=true");
}

function grd_sum_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	ds_list.filter("EXTWG == " + quote( ds_sum.GetColumn(nRow,"EXTWG"))+"&&"+
	"POSID == " + quote( ds_sum.GetColumn(nRow,"POSID")));
}


