					// if (ds_sum.GetColumn(i,"CHECK") == "1")
					// {
						// ds_list.SetColumn(j,"CHECK", "1");
					// } else {
						// ds_list.SetColumn(j,"CHECK", "0");
					// }
				// }
			// }
		// }

		ds_list.filter("EXTWG == " + quote( ds_sum.GetColumn(ds_sum.rowposition,"EXTWG"))+"&&"+
