function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);


	// 현재 조회 일자 설정 
	cal_badat_to.value = gfn_getCurrDate();
	cal_badat_fm.value = gfn_getAfterDate(cal_badat_to.value, -10);

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm02:MM0201004A_S2");

	tit_callService(
		""
		, ""
		, ""
		, "ds_city=ds_city"
		, ""
		, "fn_cdAfterQuery"
		, true);

    //  저장위치 DataSet 가져오기
    var codeArr = ["12"];
    var dsObjArr = [ds_lgort_cd];
    var addTypeArr = [G_CODE_NONE];
    var oderTypeArr = ["DTEXT1"];
    gfn_searchCommCode2(codeArr, dsObjArr, addTypeArr, oderTypeArr, true);


	if(G_USER_GROUP_M != 'M10' && G_USER_GROUP_E == 'E10')  
	{
		ufn_enableBtn("print", false);
		btn_print.visable = false;
	}
    // 엑셀 버튼 비활성화
	ufn_enableBtn("excel", false);
	cb_budat.Index = 0;

	gfn_showMsg("CI00007");
}

/********************************************************************************
* 기      능   : Callback  
********************************************************************************/
function fn_cdAfterQuery(errCode, errMsg) 
{
	ds_city.InsertRow(0);
	ds_city.SetColumn(0,"CODE","");
	ds_city.SetColumn(0,"CODE_NAME","-전체-");	
	cb_city.index = 0;
}


