/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 
	if ( gfn_isNull(cal_badat_fm, true) ) {
		gfn_showMsg("CW00001",st_badat.Text+"(시작)");
		return;
	}
	if ( gfn_isNull(cal_badat_to, true) ) {
		gfn_showMsg("CW00001",st_badat.Text+"(종료)");
		return;
	}

	if ( !gfn_checkDate( cal_badat_fm.value, cal_badat_to.value, true, cal_badat_to) ) {
		return;
	}


	if (length(ed_posid.value) > 0 && length(ed_posid.value) < 6){
		gfn_showAlert("CW00092");
		ed_posid.SetFocus();
		return;
	}


	if (length(ed_mposid.value) > 0){
		ds_cond.SetColumn(0,"POSID","");
	}

	var maxdate1 = gfn_getAfterDate(cal_badat_fm.value, 365);
	var maxdate = gfn_getAfterDate(cal_badat_fm.value, 180);
	var message_arg1 = ["조회기간","1년"];
	var message_arg = ["조회기간","180일"];
	if (length(ed_mposid.value) > 0){
		if (cal_badat_to > maxdate1)
		{
			gfn_showAlert("CW00133",message_arg1);
			return;
		}
	}
	else {
		if (cal_badat_to > maxdate)
		{
			gfn_showAlert("CW00133",message_arg);
			return;
		}
	}

/*	var maxdate = gfn_getAfterDate(cal_badat_fm.value, 180);
	var message_arg = ["조회기간","180일"];
	if (cal_badat_to > maxdate)
	{
		gfn_showAlert("CW00133",message_arg);
		return;
	}*/

	tit_clearActionInfo();
    // 프로젝트 입력시 기간 제외, 제니퍼 SQL 부하로 인한 수정 20190410
	if (length(ed_posid.value) >= 6 || length(ed_mposid.value) >= 12){
		ds_cond.SetColumn(0,"DATEGUBN","N");
		tit_addSearchActionInfo("mm02:MM0201004A_S3");
	} else {
		ds_cond.SetColumn(0,"DATEGUBN","");
		tit_addSearchActionInfo("mm02:MM0201004A_S1");
	}	


	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
/********************************************************************************
* 기      능   : 출력
********************************************************************************/

function fn_print()
{
	ds_list.fireevent = false;
	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1 && LNAME>''");

	if (ds_list_0.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		ds_list.fireevent = true;
		return;
	}

	ds_list.fireevent = true;
	ds_list_0.Sort("LNAME");

	// UbiReport Call
	Dialog("MM02::MM0201004B.xml", "", -1, -1, "Resize=true");
}
