//=========================================================================================
// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		gfn_selectGridCol("grd_list");
	} else if ( nCell == 1 ) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}


function grd_list_OnCellDBClik(obj,nRow,nCell){
		// 품목표 조회 팝업
		var arg = "HOGIN=" + quote(SubStr(obj.GetCellValue(nRow,2),0,9));
		arg += " MATNR=" + quote(obj.GetCellValue(nRow,4));
		arg += " MATKL=" + quote();

		gfn_openPopup("MM0102002B", true, arg, 420, 529);
}



function bt_multi_OnClick(obj,nX,nY)
{
	var m_posid = null;
	var mposid;

	m_posid = gfn_openPopup("WB0199062A",true);

	if (m_posid != null && obj == bt_multi && m_posid.length() > 0) {

	    mposid = " AND (";
		for (var i=0; i < m_posid.length(); i++)
		{
			if (length(m_posid[i]) > 0)
			{
				if ( i > 0)
				{
					mposid += " OR ";
				}
				mposid += " PR.POSID LIKE '"+ m_posid[i] +"%' ";
			}
		}
		mposid += ")";

		ed_mposid.value = mposid;
		bt_multi.ImageID = "btn_icon_save";
    } else {
		ed_mposid.value = "";
		bt_multi.ImageID = "btn_icon_post";
    }
}


/********************************************************************************
* 기      능   :  거래명세서 생성
********************************************************************************/
function ufn_makeGeoRae() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");

	// 필수 입력 항목 체크 
	var arg = "fa_udate_FM=" + quote(cal_badat_fm.Value);
	arg += " fa_udate_to="  + quote(cal_badat_to.Value);;
	arg += " fa_werks="+ quote("");
	arg += " fa_lgort="+ quote(ds_list_0.GetColumn(0,"LGORT"));
	arg += " fa_ekgrp="+ quote("");
	arg += " fa_posid1="+ quote("");
	arg += " fa_posid2="+ quote("");
	arg += " fa_gubn=" + quote("2");

	var fa_posid = "";
	var fa_udate = "";
	var fa_matnr = "";
	var err[2];
	if (ds_list_0.rowcount > 0) {
		fa_posid += "AND MM013C.POSID1 IN (";
		//fa_udate += "AND MM013B.UDATE IN (";
		fa_matnr += "AND MM013B.MATNR IN (";
		for (var i =0; i < ds_list_0.rowcount ; i++){

			if (i == 0){
				fa_posid +=  "'"+substr(ds_list_0.GetColumn(i,"POSID"),0,6)+"'";
				//fa_udate +=  "'"+ds_list_0.GetColumn(i,"UDATE")+"'";
				fa_matnr +=  "'"+ds_list_0.GetColumn(i,"MATNR")+"'";
			 } else if (i == ds_list_0.rowcount-1) {
				 fa_posid +=  ",'"+substr(ds_list_0.GetColumn(i,"POSID"),0,6)+"'";
				 //fa_udate +=  ",'"+ds_list_0.GetColumn(i,"UDATE")+"'";
				 fa_matnr +=  ",'"+ds_list_0.GetColumn(i,"MATNR")+"'";
			}
			else {
				fa_posid +=  ",'"+substr(ds_list_0.GetColumn(i,"POSID"),0,6)+"'";
				//fa_udate +=  ",'"+ds_list_0.GetColumn(i,"UDATE")+"'";
				fa_matnr +=  ",'"+ds_list_0.GetColumn(i,"MATNR")+"'";
			}
		}
		fa_posid += ")";
		//fa_udate += ")";
		fa_matnr += ")";
	}
	arg += " fa_posid=" + quote(fa_posid);
	arg += " fa_udate=" + quote(fa_udate);
	arg += " fa_matnr=" + quote(fa_matnr);

   gfn_openWin("MM0301002B", arg);

}

/********************************************************************************
* 기      능   :  BOX거래명세서 생성
********************************************************************************/
function ufn_makeBoxGeoRae(obj)
{
	// Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");

	// 필수 입력 항목 체크 
	var arg = "fa_udate_FM=" + quote(cal_badat_fm.Value);
	arg += " fa_udate_to="  + quote(cal_badat_to.Value);;
	arg += " fa_werks="+ quote("");
	arg += " fa_lgort="+ quote(ds_list_0.GetColumn(0,"LGORT"));
	arg += " fa_ekgrp="+ quote("");
	arg += " fa_posid1="+ quote("");
	arg += " fa_posid2="+ quote("");
	arg += " fa_gubn=" + quote("2");

	var fa_posid = "";
	var fa_extwg = "";
	var err[2];
	if (ds_list_0.rowcount > 0) {
		fa_posid += "AND MM013B.POSID IN (";
		//fa_udate += "AND MM013B.UDATE IN (";
		fa_extwg += "AND MM013B.EXTWG = ";
		for (var i =0; i < ds_list_0.rowcount ; i++){

			if (i == 0){
				fa_posid +=  "'"+ds_list_0.GetColumn(i,"POSID")+"'";
				//fa_udate +=  "'"+ds_list_0.GetColumn(i,"UDATE")+"'";
				//fa_extwg +=  "'"+"BOX20"+"'";
			 } else if (i == ds_list_0.rowcount-1) {
				 fa_posid +=  ",'"+ds_list_0.GetColumn(i,"POSID")+"'";
				 //fa_udate +=  ",'"+ds_list_0.GetColumn(i,"UDATE")+"'";
				 //fa_extwg +=  ",'"+"BOX20"+"'";
			}
			else {
				fa_posid +=  ",'"+ds_list_0.GetColumn(i,"POSID")+"'";
				//fa_udate +=  ",'"+ds_list_0.GetColumn(i,"UDATE")+"'";
				//fa_extwg +=  ",'"+"BOX20"+"'";
			}
		}
		fa_posid += ")";
		//fa_udate += ")";
		fa_extwg += "'"+"BOX20'";
	}
	arg += " fa_posid=" + quote(fa_posid);
	arg += " fa_extwg=" + quote(fa_extwg);

   gfn_openWin("MM0301004A", arg);

}
