	//  납품예정일 설정 
	cal_rdate_p_fm.value = gfn_getCurrDate();
	cal_rdate_p_to.value = gfn_getAfterDate(cal_rdate_p_fm.value, 15);

    //  전송일자 설정 
//	cal_rece_date_to.value = gfn_getCurrDate();
