function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_pro_f.index = 0;

    //  거래명세서 상세 에서 호출된 경우 조건값 입력
	if ( isExistVar("fa_rdate_p_fm", true) ) {
	   cal_rdate_p_to.value = fa_rdate_p_to;
	   cal_rdate_p_fm.value = fa_rdate_p_fm;

       cb_werks.Value = fa_werks;
	   cb_ekgrp.Value = fa_ekgrp;
	   ed_posid1.Value = fa_posid1;
	   ed_posid2.Value = fa_posid2;	   
	   fn_query();
	}
	// 완료메세지
	gfn_showMsg("CI00007");
}
