/********************************************************************************
* 기      능   : 거래명세서 전송
********************************************************************************/
function fn_send() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    //변경값 확인
	if ( !gfn_isChangeDs(ds_sum, true) ) {
		return;
	}

	//  저장에 대한 조건값 처리
	var newNo = "";

	ds_sum.fireevent = false;
	ds_list.fireevent = false;

	for ( var i = 0; i < ds_sum.rowCount(); i++ ) {
        // PO는 묶음 으로 전송되어야 함.
		// newNo = ds_sum.getColumn(i, "INVNR");

		// gfn_copyDs(ds_list, ds_list3, "INVNR="+quote(newNo));

		// for (var j = 0; j < ds_list3.rowCount(); j++) {
			// if ( toNumber(ds_list3.getColumn(j, "MENGE")) <= 0){
				// gfn_showAlert("CW00028","거래명세서 수량이 부족합니다.");
				// ds_sum.fireevent = true;
				// ds_list.fireevent = true;
				// return;
			// }


			 // BOX po대상은 한번에 거래명세서 작성
			 // if (ds_list.GetColumn(j, "PRO_F") == "1") {
				// if (length(ds_list.GetColumn(j, "WERKS")) > 0 && length(ds_list.GetColumn(j, "POSID")) > 0 &&
				    // length(ds_list.GetColumn(j, "EXTWG")) > 0){
					// ds_list.SetColumn(j,"CHECK", "0");
					// gfn_showAlert("CW00031",ds_list.GetColumn(j, "EXTWG"));
					// ds_sum.fireevent = true;
					// ds_list.fireevent = true;
					// return;
				// }
			// }
		// }

		// 처리상태가 1이고 PO삭제일이 '', 수량이 >0 인 자료만 전송가능. (Box 자재 포함여부확인)
		if( ds_sum.getColumn(i, "CHECK") == "1") { 

			newNo = ds_sum.getColumn(i, "INVNR");

			gfn_copyDs(ds_list, ds_list3, "INVNR="+quote(newNo));

			for (var j = 0; j < ds_list3.rowCount(); j++) {

				if ( toNumber(ds_list3.getColumn(j, "MENGE")) <= 0){
					gfn_showAlert("CW00028","거래명세서 수량이 부족합니다.");
					ds_sum.fireevent = true;
					ds_list.fireevent = true;
					return;
				}

				if (length(ds_list.GetColumn(j, "DDATE")) > 0) {
					if (ds_list.GetColumn(j, "PRO_F") == "2"){
						ds_sum.SetColumn(i,"CHECK", "0");
						gfn_showAlert("CW00027");
						ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
						ds_sum.fireevent = true;
						ds_list.fireevent = true;
						return;   
					}
					ds_sum.SetColumn(i,"CHECK", "0");
					gfn_showAlert("CW00028","상태 확인");
					ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
					ds_sum.fireevent = true;
					ds_list.fireevent = true;
					return;
				} 

			}

			 // working day -3일 이전에는 전송불가 , 납기일 이후에 전송불가 
			 if ( toNumber( gfn_getCurrDate()) <= toNumber( ds_sum.GetColumn(i,"UDATE")) &&  
			      toNumber( ds_sum.GetColumn(i,"UDATE")) > toNumber(wdate)) {
				gfn_showAlert("CW00029",ds_sum.GetColumn(i,"UDATE"));
				ds_sum.SetColumn(i,"CHECK", "0");
				ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
				ds_sum.fireevent = true;
				ds_list.fireevent = true;
				return;
			  } 
			  // 현재일 이전의 납기는 모두 받아준다...2008.05.13
			 // else if ( toNumber( gfn_getCurrDate()) > toNumber(ds_sum.GetColumn(i,"UDATE")) ) {
				// gfn_showAlert("CW00030",ds_sum.GetColumn(i,"UDATE"));
				// ds_sum.SetColumn(i,"CHECK", "0");
				// ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
				// ds_sum.fireevent = true;
				// ds_list.fireevent = true;
				// return;
			 // }

		}	// end if 
	}	// end for


	if (!gfn_showConfirm("CI00010")) {
		ds_sum.fireevent = true;
		ds_list.fireevent = true;
		return;
	}
	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_sum2, "CHECK=1");

	ds_sum.fireevent = true;
	ds_list.fireevent = true;

	tit_clearActionInfo();

	// 저장 처리 
	tit_addMultiActionInfo("mm03:MM0301001A_U1");

	tit_callService(
        ""
        , ""
        , "ds_sum=ds_sum2:A"
        , ""
        , ""
        , "fn_afterSave");
}

/********************************************************************************
* 기      능   : 거래명세서 전송취소 
********************************************************************************/
function fn_cancel() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    //변경값 확인
	if ( !gfn_isChangeDs(ds_sum, true) ) {
		return;
	}

	ds_list.fireevent= false;

	//  저장에 대한 조건값 처리-PRO_F=2, DDATE=''인것만.
	for ( var i = 0; i < ds_sum1.rowcount ; i++ ) {

		if (ds_sum.GetColumn(i,"CHECK") == "1") {
			newNo = ds_sum.getColumn(i, "INVNR");
			gfn_copyDs(ds_list, ds_list3, "INVNR="+quote(newNo));

			for(var j =0; j < ds_list3.rowcount; j++){
				 if ((ds_list3.GetColumn(j, "PRO_F") != "2" && ds_list3.GetColumn(j, "PRO_F") != "4") || ds_list.GetColumn(j, "DDATE") != "" ) {
					gfn_showMsg("CW00022",ds_list.getColumn(j, "INVNR")+"의 상태 또는 삭제일");
					ds_sum.SetColumn(i,"CHECK", "0");
					ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
					ds_list.fireevent= true;
					return;
				 }
			}
		}
	}
	ds_list.fireevent= true;

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_sum2, "CHECK=1");

	if (!gfn_showConfirm("CI00011")) {
		return;
	}

	tit_clearActionInfo();

	// 저장 처리 
	tit_addMultiActionInfo("mm03:MM0301001A_U2");

	tit_callService(
        ""
        , ""
        , "ds_sum=ds_sum2:A"
        , ""
        , ""
        , "fn_afterSave");
}

