/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showSearchMsg(grd_sum);

    // 조회 결과가 있으면 엑셀 버튼 활성화
	if ( ds_sum.rowCount() > 0 ) {
		ufn_enableBtn("excel", true);
		wdate = gfn_getWorkingDate(15, ds_list.GetColumn("0","WERKS"));
		ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(0,"INVNR")));
	} else {
		ufn_enableBtn("excel", false);
		gfn_showMsg("CW00010");
	}
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

