/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_sum_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 전체 선택 취소
		gfn_selectAllGrid(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 전체 선택 취소
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 거래명세서 번호 더블클릭시 거래명세서 상세 화면조회
********************************************************************************/
function grd_sum_OnCellDBClik(obj,nRow,nCell){
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 
	if ( gfn_isNull(cal_rdate_p_fm, true) ) {
		gfn_showMsg("CW00001","납품예정일(시작)");
		return;
	}
	if ( gfn_isNull(cal_rdate_p_to, true) ) {
		gfn_showMsg("CW00001","납품예정일(종료)");
		return;
	}

	if ( !gfn_checkDate( cal_rdate_p_fm.value, cal_rdate_p_to.value, true, cal_rdate_p_to) ) {
		return;
	}
/*	if ( gfn_isNull(cb_werks, true) ) {
		gfn_showMsg("CW00001","Plant");
		return;
	}*/

	var arg = "fa_udate_fm=" + quote(cal_rdate_p_fm.Value);
	arg += " fa_udate_to=" + quote( cal_rdate_p_to.Value) ;
	arg += " fa_werks=" + quote( cb_werks.Value );
	arg += " fa_lgort=" + quote( ds_sum.GetColumn(nRow,"LGORT"));
	arg += " fa_ekgrp=" + quote( obj.GetCellValue(nRow,2) );
	arg += " fa_invnr=" + quote( obj.GetCellValue(nRow,1) );
	arg += " fa_posid1=" + quote("");
	arg += " fa_posid2=" + quote("");

   gfn_openWin("MM0301002A", arg);
}
// 자재조회
