   if (nCell == 2){
      gfn_openPopup("MM0901001A", true, "fa_matnr=" + quote(obj.GetCellValue(nRow,2)), 420, 529);
   }
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_sum_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{ 
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	ds_list.fireevent = false;
	if ( strColumnID == "CHECK") {
		 if (obj.GetColumn(nRow, "PRO_F") != "1" ||
		            obj.GetColumn(nRow, "DDATE") != "") {
			if (obj.GetColumn(nRow, "PRO_F") == "2"){
				gfn_showMsg("CW00027");
				ds_list.fireevent = true;
				return;   
			}
			gfn_showMsg("CW00028","생성 상태가 아닙니다.");
			obj.SetColumn(nRow,"CHECK", "0");
			ds_list.fireevent = true;
			return;
		 }
	}
	ds_list.fireevent = true;
}

function grd_sum_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(nRow,"INVNR")));
}

function Button3_OnClick(obj)
{
	ds_list.fireevent = false;
	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_georea, "CHECK=1");

	if (ds_georea.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		ds_list.fireevent = true;
		return;
	}

	gfn_copyDs(ds_list, ds_list2);

	for (var i =0; i < ds_georea.rowcount ; i++){

		for (var j=0; j< ds_list2.rowcount; j++){
			if (ds_list2.GetColumn(j,"INVNR") == ds_georea.GetColumn(i,"INVNR") && ds_list2.GetColumn(j,"PRO_F") == "1"){
				gfn_showAlert("CW00097");
				ds_list.fireevent = true;
				return;
			}
			if (ds_list2.GetColumn(j,"INVNR") == ds_georea.GetColumn(i,"INVNR") && ds_list2.GetColumn(j,"PRO_F") >= "2") {
				ds_list2.SetColumn(j,"CHECK","1");
			}
		}
	}

	gfn_copyDs(ds_list2, ds_list3, "CHECK=1");

	// UbiReport Call
	Dialog("MM03::MM0301001B.xml", "");
	ds_list.fireevent = true;
}
