/********************************************************************************
* 기      능   : 거래명세서 전송
********************************************************************************/
function fn_send() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    //변경값 확인
	if ( !gfn_isChangeDs(ds_sum, true) ) {
		return;
	}

	//  저장에 대한 조건값 처리
	var newNo = "";

	ds_sum.fireevent = false;
	ds_list.fireevent = false;
	var err[2];

	var arr = split(G_ORG_USER_INFO, "^", true); 
	var epm = ds_epm.searchRow("CODE== " + quote(arr[0]));

	for ( var i = 0; i < ds_sum.rowCount(); i++ ) 
	{
		// 처리상태가 1이고 PO삭제일이 '', 수량이 >0 인 자료만 전송가능. (Box 자재 포함여부확인)
		if( ds_sum.getColumn(i, "CHECK") == "1") { 

			if (ds_sum.GetColumn(i,"BIGO") == "1") {
			    err[0] = grd_sum.GetCellProp("head",1,"text");
			    err[1] = ds_sum.GetColumn(i,"INVNR");
				gfn_showAlert("CW00184",err);
				return;
			}	

			// 미수현장 확인
			// 보수 자재의 경우 2000번 플랜트는 제한 제외 - 20130930 조요환과장 요청 HSS
			if (ds_sum.getColumn(i,"WERKS") != "2000" && ds_sum.getColumn(i,"BIGO") == "2")
			{
			   ds_list.fireevent = true;
			   gfn_showAlert("CW00071","미수현장 거래명세서 번호:"+ds_sum.GetColumn(i,"INVNR"));
			   ds_sum.SetColumn(i,"CHECK", "0");
			   return;
			} 	

			// 바코드 태그생성 확인
			// if (ds_sum.getColumn(i,"INVTAG") != "Y")
			// {
			   // ds_list.fireevent = true;
			   // gfn_showAlert("CW00113","바코드 태그 [거래명세서 번호:"+ds_sum.GetColumn(i,"INVNR")+"]");
			   // ds_sum.SetColumn(i,"CHECK", "0");
			   // return;
			// } 	

			newNo = ds_sum.getColumn(i, "INVNR");

			gfn_copyDs(ds_list, ds_list3, "INVNR="+quote(newNo));

			for (var j = 0; j < ds_list3.rowCount(); j++) {

				if ( toNumber(ds_list3.getColumn(j, "MENGE")) <= 0){
					gfn_showAlert("CW00028","거래명세서 수량이 부족합니다.");
					ds_sum.fireevent = true;
					ds_list.fireevent = true;
					return;
				}
				if (ds_list3.GetColumn(j, "PRO_F") != "1"){
					ds_sum.SetColumn(i,"CHECK", "0");
					gfn_showAlert("CW00027");
					ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
					ds_sum.fireevent = true;
					ds_list.fireevent = true;
					return;   
				}
				if (length(ds_list3.GetColumn(j, "DDATE")) > 0) {
					if (ds_list3.GetColumn(j, "PRO_F") == "2"){
						ds_sum.SetColumn(i,"CHECK", "0");
						gfn_showAlert("CW00027");
						ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
						ds_sum.fireevent = true;
						ds_list.fireevent = true;
						return;   
					}
					ds_sum.SetColumn(i,"CHECK", "0");
					gfn_showAlert("CW00028","상태 확인");
					ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
					ds_sum.fireevent = true;
					ds_list.fireevent = true;
					return;
				} 
			// 2010.06.21 박찬욱 요청으로 TECO 제한 로직 적용
				if (ds_list3.GetColumn(j, "WERKS") == "1000"  && length(ds_list3.GetColumn(j, "P_END")) > 0) {
					grd_sum.CurrentRow = i;
					ds_sum.SetColumn(i,"CHECK", "0");
					gfn_showAlert("CW00028","TECO 현장");
					ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
					ds_sum.fireevent = true;
					ds_list.fireevent = true;
					return;
				} 
				// if ( ds_sum.GetColumn(i,"LGORT") == "8000" 
					// &&  toNumber( gfn_getCurrDate()) < toNumber( ds_sum.GetColumn(i,"UDATE"))) 
				// 납기 통제
				if (epm == -1)
				{
					// 신규통제 2020.08.13 추가
					if ((G_VEND_CODE == '2100152155' || G_VEND_CODE == '1252286309' || G_VEND_CODE == '1178113200' || G_VEND_CODE == '3128126519' || 
						 G_VEND_CODE == '1348658793' || G_VEND_CODE == '3038153273' || G_VEND_CODE == '1368126948' || G_VEND_CODE == '1378167262' || 
						 G_VEND_CODE == '1258199290' || G_VEND_CODE == '1348156956' || G_VEND_CODE == '8328100309')
						 && (ds_sum.GetColumn(i,"LGORT") != "1002" && ds_sum.GetColumn(i,"LGORT") != "9000")
						 && toNumber( gfn_getWorkingDate(1,"1000",gfn_getCurrDate())) < toNumber( ds_sum.GetColumn(i,"UDATE")))
					{
						for (var k = 0; k < ds_list3.rowCount(); k++) 
						{

							if (ds_list3.GetColumn(k,"INVNR") == ds_sum.GetColumn(i,"INVNR") &&
								(SubStr(ds_list3.GetColumn(k,"POSID"),0,1) != 'C' && SubStr(ds_list3.GetColumn(k,"POSID"),0,1) != 'E'
								 && SubStr(ds_list3.GetColumn(k,"POSID"),0,1) != 'F')) 
							{
								nRow = ds_tm.searchRow("CODE== " + quote(ds_list3.GetColumn(k,"MATNR")));
								if ( ds_list3.GetColumn(k,"INVNR") == ds_sum.GetColumn(i,"INVNR") && nRow == -1) 
								{
									gfn_showAlert("CW00029", ds_list3.GetColumn(k,"UDATE")+"("+ds_list3.GetColumn(k,"MATNR")+")");
									ds_sum.SetColumn(i,"CHECK", "0");
									ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
									ds_sum.fireevent = true;
									ds_list.fireevent = true;
									return;
								}
							}
						}
					}
					// 납기통제 2008.12.22 수정
					else if ((G_VEND_CODE != '2100152155' && G_VEND_CODE != '1252286309' && G_VEND_CODE != '1178113200' && G_VEND_CODE != '3128126519' &&
							  G_VEND_CODE != '1348658793' && G_VEND_CODE != '3038153273' && G_VEND_CODE != '1368126948' && G_VEND_CODE != '1378167262' &&
							  G_VEND_CODE != '1258199290' && G_VEND_CODE != '1348156956' && G_VEND_CODE != '8328100309')
							  && (ds_sum.GetColumn(i,"LGORT") != "1002" && ds_sum.GetColumn(i,"LGORT") != "9000")
							  && toNumber( gfn_getCurrDate()) < toNumber( ds_sum.GetColumn(i,"UDATE"))) 
					{
						for (var k = 0; k < ds_list3.rowCount(); k++) 
						{

							if (ds_list3.GetColumn(k,"INVNR") == ds_sum.GetColumn(i,"INVNR") &&
								(SubStr(ds_list3.GetColumn(k,"POSID"),0,1) != 'C' && SubStr(ds_list3.GetColumn(k,"POSID"),0,1) != 'E'
								 && SubStr(ds_list3.GetColumn(k,"POSID"),0,1) != 'F')) 
							{
								nRow = ds_tm.searchRow("CODE== " + quote(ds_list3.GetColumn(k,"MATNR")));
								if ( ds_list3.GetColumn(k,"INVNR") == ds_sum.GetColumn(i,"INVNR") && nRow == -1) 
								{
									gfn_showAlert("CW00029", ds_list3.GetColumn(k,"UDATE")+"("+ds_list3.GetColumn(k,"MATNR")+")");
									ds_sum.SetColumn(i,"CHECK", "0");
									ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
									ds_sum.fireevent = true;
									ds_list.fireevent = true;
									return;
								}
							}
						}
					}
					// 선박용의 경우 working day 기준 30일 이내만 입고
					if ( toNumber( wdate) < toNumber( ds_sum.GetColumn(i,"UDATE"))) 
					{
						for (var k = 0; k < ds_list3.rowCount(); k++) 
						{

							if (ds_list3.GetColumn(k,"INVNR") == ds_sum.GetColumn(i,"INVNR") && SubStr(ds_list3.GetColumn(k,"POSID"),0,1) == 'E') 
							{

								if ( ds_list3.GetColumn(k,"INVNR") == ds_sum.GetColumn(i,"INVNR")) 
								{
									gfn_showAlert("CW00029", ds_list3.GetColumn(k,"UDATE")+"("+ds_list3.GetColumn(k,"MATNR")+")");
									ds_sum.SetColumn(i,"CHECK", "0");
									ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
									ds_sum.fireevent = true;
									ds_list.fireevent = true;
									return;
								}
							}
						}
					}
				}

			}
		}	// end if 
	}	// end for

	// JQPR 입고예정일 미입력 개수 확인
	if(ds_cnt.GetColumn(0, "CNT") > 0) {
		fn_afterChkJajae();
		return;
	}

	// JAMB 출하예정일 미입력 갯수 확인
	if(ds_cnt2.GetColumn(0, "CNT") > 0) {
		fn_afterChkJamb();
		return;
	}

	if (!gfn_showConfirm("CI00010")) {
		ds_sum.fireevent = true;
		ds_list.fireevent = true;
		return;
	}
	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_sum2, "CHECK=1");

	ds_sum.fireevent = true;
	ds_list.fireevent = true;

	tit_clearActionInfo();

	// 저장 처리 
	tit_addMultiActionInfo("mm03:MM0301001A_U1");

	tit_callService(
        ""
        , ""
        , "ds_sum=ds_sum2:A"
        , ""
        , ""
        , "fn_afterSave");
}

/********************************************************************************
* 기      능   : 거래명세서 전송취소 
********************************************************************************/
function fn_cancel() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    //변경값 확인
	if ( !gfn_isChangeDs(ds_sum, true) ) {
		return;
	}

	ds_list.fireevent= false;

	//  저장에 대한 조건값 처리-PRO_F=2, DDATE=''인것만.
	for ( var i = 0; i < ds_sum.rowcount ; i++ ) {

		if (ds_sum.GetColumn(i,"CHECK") == "1") {
			newNo = ds_sum.getColumn(i, "INVNR");
			gfn_copyDs(ds_list, ds_list3, "INVNR="+quote(newNo));

			for(var j =0; j < ds_list3.rowcount; j++){
				 if ((ds_list3.GetColumn(j, "PRO_F") != "2" && ds_list3.GetColumn(j, "PRO_F") != "4") || length(ds_list3.GetColumn(j, "DDATE")) > 0 ) {
					gfn_showMsg("CW00022",ds_list3.getColumn(j, "INVNR")+"의 상태 또는 삭제일");
					ds_sum.SetColumn(i,"CHECK", "0");
					ds_list.filter("INVNR == " + quote( ds_sum.GetColumn(i,"INVNR")));
					ds_list.fireevent= true;
					return;
				 }
			}
		}
	}
	ds_list.fireevent= true;

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_sum2, "CHECK=1");

	if (!gfn_showConfirm("CI00011")) {
		return;
	}

	tit_clearActionInfo();

	// 저장 처리 
	tit_addMultiActionInfo("mm03:MM0301001A_U2");

	// zmmt017 _Z 저장 처리 
	tit_addMultiActionInfo("mm03:MM0301001A_U3");

	tit_callService(
        ""
        , ""
        , "ds_sum=ds_sum2:A"
        , ""
        , ""
        , "fn_afterCancel");
}

