	if (ds_georea.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		ds_list.fireevent = true;
		return;
	}

	gfn_copyDs(ds_list, ds_list2);

	for (var i =0; i < ds_georea.rowcount ; i++){
		//alert("TOTCNT는 " + ds_georea.getColumn(i,"TOTCNT"));
		//alert("예외수량은 " + ds_georea.getColumn(i,"EXPCOUNT")) ;

		if( G_VEND_CODE != "1078150926" &&  G_VEND_CODE != "6028151774"  ) // 크레탐은 사전 점검 예외 처리, 씨넷 예외 처리 추가 
