					   ds_georea.SetColumn(i,"CHECK", "0");
					   return;
				  }
				}
			}
			else {
				//alert("exception!!!!");
			}
		}

		for (var j=0; j< ds_list2.rowcount; j++){
			if (ds_list2.GetColumn(j,"INVNR") == ds_georea.GetColumn(i,"INVNR") && ds_list2.GetColumn(j,"PRO_F") == "1"){
				gfn_showAlert("CW00097");
				ds_list.fireevent = true;
				return;
			}
			if (ds_list2.GetColumn(j,"INVNR") == ds_georea.GetColumn(i,"INVNR") && ds_list2.GetColumn(j,"PRO_F") >= "2") {
				ds_list2.SetColumn(j,"CHECK","1");

             	// 2010.12.20 박찬욱 요청으로 TECO 제한 다시 로직 적용
				if (ds_list2.GetColumn(j, "WERKS") == "1000"  && length(ds_list2.GetColumn(j, "P_END")) > 0 
				    && (length(ds_list2.GetColumn(j,"MBLNR")) <= 0  && length(ds_list2.GetColumn(j,"CA_DT")) <= 0)) {
					gfn_showAlert("CW00071","TECO 현장");
					ds_list.filter("INVNR == " + quote( ds_georea.GetColumn(i,"INVNR")));
					ds_list.fireevent = true;
					return;
