}


/********************************************************************************
* 기      능   : Barcode TAG생성 
********************************************************************************/
var tag_save = false;
function fn_save_tag() {
    // 자재인식으로 인한 제어 210319 yj
    alert("태그생성은 자재인식시스템에서 진행해주시기 바랍니다.");
    return;

    // Message 영역 초기화

	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";
	ds_sum3.ClearData();

	// 저장할 데이터셋을 복사

	for (var i =0; i < ds_sum.rowcount; i++) {
		if (ds_sum.GetColumn(i,"CHECK") == "1") {

			// 바코드 태그생성 확인
			if (ds_sum.getColumn(i,"INVTAG") == "Y")
			{
			   ds_list.fireevent = true;
			   gfn_showAlert("CW00121","바코드 태그 생성[거래명세서 번호:"+ds_sum.GetColumn(i,"INVNR")+"]");
			   ds_sum.SetColumn(i,"CHECK", "0");
			   return;
			} 	

			ds_sum3.AddRow();
			ds_sum3.SetColumn(ds_sum3.currow,"INVNR", ds_sum.GetColumn(i,"INVNR"));
			ds_sum3.SetColumn(ds_sum3.currow,"LIFNR", ds_sum.GetColumn(i,"LIFNR"));
		}
	}

	if (ds_sum3.rowcount == 0) {
		gfn_showAlert("CW00116","거래명세서를");
		return;
	}

	if (!gfn_showConfirm("CI00008")) {
		return;
	}
	if (tag_save)
	{

		gfn_showAlert("CW00149");
		return; 
	}

	tag_save = true;

	tit_clearActionInfo();

	// 저장 처리 
	tit_addMultiActionInfo("MM0301003A_ACT");


	// 저장 처리 
	tit_callService(
        "MM0301003A_ACT"
        , "save"
        , "ds_sum3=ds_sum3:A"
        , "ds_error=ds_error"
        , ""
        , "fn_afterTagSave");	
}

function fn_afterTagSave(){
	tag_save = false;

	if ( errMsg == "INVALID_DATA" ) {
		gfn_showAlert("CW00182", errMsg);
	} else if ( ds_error.rowcount() > 0 ) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function printBarcodeOnClick(obj)
{
	// 자재인식으로 인한 제어 210319 yj
    alert("태그출력은 자재인식시스템에서 진행해주시기 바랍니다.");
    return;

    //변경값 확인
	if ( !gfn_isChangeDs(ds_sum, true) ) {
		return;
	}

	ds_sum2.ClearData();

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_sum, ds_sum2, "CHECK=1");

	if (ds_sum2.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}

	var fa_invnr = "";

	if (ds_sum2.rowcount > 0) {
		fa_invnr += "AND M17.INVNR IN (";
		for (var i =0; i < ds_sum2.rowcount ; i++){
			// 오발주 확인
			if (ds_sum2.GetColumn(i,"INVTAG") != "Y") {
				gfn_showAlert("CW00124", "바코드 태그 생성[거래명세서 번호:"+ds_sum.GetColumn(i,"INVNR")+"] 후");
				return;
			}		

			if (i == 0){
				fa_invnr +=  "'"+ds_sum2.GetColumn(i,"INVNR")+"'";
			 } else if (i == ds_sum2.rowcount-1) {
				 fa_invnr +=  ",'"+ds_sum2.GetColumn(i,"INVNR")+"'";
			}
			else {
				fa_invnr +=  ",'"+ds_sum2.GetColumn(i,"INVNR")+"'";
			}
		}
		fa_invnr += ")";
	}

	tit_clearActionInfo();

//	tit_addSearchActionInfo("mm03:MM0301003A_S5");
	if(cb_detail.Value == "1")
	{
		tit_addSearchActionInfo("mm03:MM0301003A_S6");
	} else {
		tit_addSearchActionInfo("mm03:MM0301003A_S5");
	}

	tit_callService(
        ""
        , ""
        , ""
        , "ds_tag=ds_tag ds_tag_detail=ds_tag_detail"
        , "INVNR="+quote(fa_invnr)
        , "fn_afterTagQuery"
        , true);

}

function fn_afterTagQuery(errCode, errMsg){

	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		// UbiReport Call
		ds_tag_0.ClearData();
		ds_tag_detail_0.ClearData();
		for(var i = 0; i < cb_tagprint.Value; i++) {
			ds_tag_0.AppendDataset(ds_tag);
			if(cb_detail.Value == "1")
			{
				ds_tag_detail_0.AppendDataset(ds_tag_detail);
			}
		}
		ds_tag_0.Sort("INVNR,INVTAG");
		ds_tag_detail_0.Sort("INVNR,INVTAG,MATNR");

		var arg = "fa_tagtype="+quote(cb_tagtype.Value) + " fa_detail="+quote(cb_detail.Value);

		Dialog("MM03::MM0301003D.xml", arg, -1, -1, "Resize=true");
	}
}

/********************************************************************************
* 기      능   : 특정edit 입력 문자 검사
********************************************************************************/
function chk_edcode(str) {

	var pattern = "[^A-z0-9 ]+"; // 영문,숫자가 아닌 문자 filtering하는 정규식
	var reg = CreateRegExp(pattern, "g");
	var str_notnum = reg.Exec(str);

	if ( str_notnum.length >0) {
		// 영문,숫자가 아닌 문자 입력 불가
		return false;
	}
	return true;
}
