function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 0) {
		// 그리드 표시할 컬럼 선택 
		gfn_multiSortGrid(obj, false, 1);
	}
	else if ( nCell == 1 ) {
		// 전체 선택 취소
		gfn_selectAllGrid2(obj,1,1,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}



/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{ 
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	switch( strColumnID ) {
		case "FLAG":
			break; 
		case "CHECK":
					ds_list.FireEvent = false;
					if (ds_list.GetColumn(nRow, "FLAG") == "I" && ds_list.GetColumn(nRow,"CHECK") == "0") {
						ds_list.SetColumn(nRow,"FLAG","");
						ds_list.SetColumn(nRow,"CHECK","0");
						ds_list.SetColumn(nRow,"CHA",ds_list.GetOrgColumn(nRow,"CHA"));
						ds_list.FireEvent = true;
						return;
					} else if (ds_list.GetColumn(nRow,"CHECK") == "0") {
						if (ds_list.GetColumn(nRow, "FLAG") != "") {
							ds_list.SetColumn(nRow, "FLAG","");
						}
						ds_list.FireEvent = true;
						return;
					}

					ds_list.FireEvent = true;	        
	        break;
		case "CHA":// 입고요청수량 
			obj.FireEvent = false;
		    var meval = toNumber(obj.GetColumn(nRow,"BMENGE")) - toNumber(obj.GetColumn(nRow,"GMENGE"));

			if ( ds_list.GetOrgColumn(nRow,"CHA") == varNewValue ) {
    			obj.SetColumn(nRow, "FLAG", "");
    			obj.SetColumn(nRow,"CHECK", "0");
				obj.FireEvent = true;
				return;
			}
			else if ( toNumber(varNewValue) > toNumber(meval) || toNumber(varNewValue) <= 0)
			{
			   var arr = ["입고요청수량은 0 이상의 값으로", "반출 잔량"];
			   gfn_showAlert("CW00015",arr);
			   obj.SetColumn(nRow, "CHA", obj.GetOrgBuffColumn(nRow,"CHA"));
			   obj.SetColumn(nRow, "FLAG", "");
			   obj.SetColumn(nRow,"CHECK", "0");
	 		   obj.FireEvent = true;
			   return;
			}

			// for (var i = 0; i < ds_list.GetRowCount(); i++) {
				// if (ds_list.GetColumn(i, "FLAG") == "D") {
				    // obj.SetColumn(nRow, "CHA", toNumber(obj.GetOrgBuffColumn(nRow,"CHA")));
					// gfn_showAlert("CW00017");
					// obj.FireEvent = true;
					// return;
				// }
			}
			obj.FireEvent = true;

			obj.SetColumn(nRow, "FLAG", "I");
			obj.SetColumn(nRow, "CHECK", "1");
    		break;
	}

}




/********************************************************************************
* 기      능   : Barcode TAG생성 
********************************************************************************/
function fn_save_tag() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";
	ds_list_0.ClearData();

	// 저장할 데이터셋을 복사
	ds_list.fireevent = false;	
	for (var i =0; i < ds_list.rowcount; i++) {
		if (ds_list.GetColumn(i,"CHECK") == "1") {

			// 바코드 태그생성 확인
			if (length(ds_list.getColumn(i,"DOCNO")) > 0)
			{
			   ds_list.fireevent = true;
			   gfn_showAlert("CW00121","바코드 태그 생성[문서 번호:"+ds_list.GetColumn(i,"DOCNO")+"]");
			   ds_list.SetColumn(i,"CHECK", "0");
			   ds_list.fireevent = true;
			   return;
			}
			if (toNumber(ds_list.getColumn(i,"CHA")) <= 0)
			{
				var arr = ["입고요청수량은 0 이상의 값으로", "반출 잔량"];
				gfn_showAlert("CW00015",arr);
				ds_list.SetColumn(i, "FLAG", "");
				ds_list.SetColumn(i,"CHECK", "0");
				ds_list.fireevent = true;
				return;
			}
		}
	}
	ds_list.fireevent = true;

	// 저장할 데이터셋 복사
	gfn_copyDS(ds_list,ds_list_0, "CHECK=1 && FLAG='I'");

	if (!gfn_showConfirm("CI00008")) {
		return;
	}

	tit_clearActionInfo();
	ds_key.ClearData();
	// 저장 처리 
	tit_addSearchActionInfo("mm03:MM0301006A_T1",true);

	tit_addSearchActionInfo("mm03:MM0301006A_S2",true);
	tit_addSaveActionInfo(
        "mm03:MM0301006A_I1"
        , ""
        , ""
        , "FLAG"
		,""
		, 0
		, "");

//   call 서비스에서 commit실행하여 commit 필요없음.

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list_0:A ds_cond=ds_cond ds_key=ds_key"
        , "ds_key=ds_key"
        , ""
        , "fn_afterTagSave");	
}

function fn_afterTagSave(errCode, errMsg){
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}
	fn_query();
}

function printBarcodeOnClick(obj)
{

    //변경값 확인
	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}

	ds_list_0.ClearData();

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_list, ds_list_0, "CHECK=1");

	if (ds_list_0.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}

	var fa_docno = "";

	if (ds_list_0.rowcount > 0) {
		fa_docno += "AND DOCNO IN (";
		for (var i =0; i < ds_list_0.rowcount ; i++){
			// 오발주 확인
			if (length(ds_list_0.GetColumn(i,"DOCNO")) < 1) {
				gfn_showAlert("CW00124", "바코드 태그 생성 후");
				return;
			}		

			if (i == 0){
				fa_docno +=  "'"+ds_list_0.GetColumn(i,"DOCNO")+"'";
			 } else if (i == ds_list_0.rowcount-1) {
				 fa_docno +=  ",'"+ds_list_0.GetColumn(i,"DOCNO")+"'";
			}
			else {
				fa_docno +=  ",'"+ds_list_0.GetColumn(i,"DOCNO")+"'";
			}
		}
		fa_docno += ")";
	}

	tit_clearActionInfo();

	tit_addSearchActionInfo("mm03:MM0301006A_S3");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_tag=ds_tag"
        , "DOCNO="+quote(fa_docno)
        , "fn_afterTagQuery"
        , true);

}

function fn_afterTagQuery(errCode, errMsg){

	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		// UbiReport Call
		Dialog("MM03::MM0301006B.xml", "", -1, -1, "Resize=true");
	}
}
