function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	// 공급 업체명 설정 
	ed_lifnrName.value = G_VEND_NAME;
	ds_cond.setColumn(0, "LIFNR", G_VEND_CODE_M);
    ds_cond.setColumn(0, "MANDT", G_MANDT);

    // 코드 DataSet 가져오기
    var codeArr = ["5","10","11"];
    var dsObjArr = [ds_state_cd,ds_ekgrp_cd,ds_werks_cd];
    var addTypeArr = [G_CODE_NONE,G_CODE_ALL,G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);


 //   ds_lgort_cd.setColumn(0, "CODE", "");


    //  저장위치 DataSet 가져오기
    var codeArr = ["12"];
    var dsObjArr = [ds_lgort_cd];
    var addTypeArr = [G_CODE_ALL];
    var oderTypeArr = ["DTEXT1"];
    gfn_searchCommCode2(codeArr, dsObjArr, addTypeArr, oderTypeArr, true);

	// 현재 조회 일자 설정 
	cal_udate_fm.value = gfn_getCurrDate();
	cal_udate_to.value = gfn_getAfterDate(cal_udate_fm.value, 15);

    // 엑셀 버튼 비활성화
	ufn_enableBtn("excel", false);
	ufn_enableBtn("save", false);

	gfn_showMsg("CI00007");
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	//  일자 조건  설정 
	cb_dgbn.Index = 0;

	//  Plant  설정 
	cb_werks.Index = 0;

	//  저장위치  설정 
	cb_lgort.Index = 0;

	//  구매그룹  설정 
	cb_ekgrp.Index = 0;

	//  조회구분 설정 
	cb_gubn.Index = 0;

	//  입고구분 설정 
	cb_egubn.Index = 0;

	//  분류  설정 
	cb_kind.Index = 0;

	var nRow = gds_auth.searchRow("PGM_ID== " + quote(substr(F_PGM_ID,0,10)));
	var user_grp = gfn_getOrgUserGroup();
	if (user_grp > "10" && gds_auth.getColumn(nRow, "MULT") == "Y" )
	{
		btn_popCorp.enable=true;
		btn_popCorp.Visible=true;
	}
}
