function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}



function grd_list_OnCellDBClik(obj,nRow,nCell){
}


function btn_ChangeCorp(obj)
{
	var arr= null;
	arr = gfn_openPopupUrl("WB01::CommChangeCorp.xml", true);	

	if (arr != null && obj == btn_popCorp && length(arr[2]) > 0 ) {
		ed_lifnrName.value = arr[3];
		ds_cond.setColumn(0, "LIFNR", arr[2]);
    }

}


var col = "";
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = rect[1] +110 ;
	cal_temp.Left = rect[0]; 
	cal_temp.Visible = true;

	if (nCell = 2)
	{
		cal_temp.value = ds_list.getColumn(nRow, "IPSDT");	
		col = "IPSDT";
	}
	if (nCell = 3)
	{
		cal_temp.value = ds_list.getColumn(nRow, "RECDT");	
		col = "RECDT";
	}
	if (nCell = 4)
	{
		cal_temp.value = ds_list.getColumn(nRow, "CAUSE");	
		col = "CAUSE";
	}

	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	ds_list.setColumn(ds_list.row, col, strText);

	col = "";
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
	col = "";
}
