function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	// 공급 업체명 설정 
	user_grp = gfn_getUserGroup();
	if(user_grp == '10') 
	{
		cb_lifnr.Value = G_VEND_CODE;
		cb_lifnr.Enable = false;
	} 

	cb_dgbn.index = 0;
	cb_gubn.index = 0;

	// 현재 조회 일자 설정 
	cal_date_fm.value = gfn_getCurrDate();
	cal_date_to.value = gfn_getAfterDate(cal_date_fm.value, 7);

    // 엑셀 버튼 비활성화
	ufn_enableBtn("excel", false);

	gfn_showMsg("CI00007");
}
