/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";


	// 필수 입력 항목 체크 

	if ( cb_lifnr.index = -1 ) {
		return;
	}

	if ( gfn_isNull(cal_date_fm, true) ) {
		gfn_showMsg("CW00001","납기일자(시작)");
		return;
	}
	if ( gfn_isNull(cal_date_to, true) ) {
		gfn_showMsg("CW00001","납기일자(종료)");
		return;
	}

	if ( !gfn_checkDate( cal_date_fm.value, cal_date_to.value, true, cal_date_to) ) {
		return;
	}

	var maxdate = gfn_getAfterDate(cal_udate_fm.value, 180);
	var message_arg = ["조회기간","180일"];
	if (cal_udate_to > maxdate)
	{
		gfn_showAlert("CW00133",message_arg);
		return;
	}

	ds_cond.SetColumn(0,"UDATE_FM", gfn_getWorkingDate(-2,"1000",ds_cond.GetColumn(0,"DATE_FM")));  // 출하일은 입고 예정일 -2 WD로 생각하여 조회
	ds_cond.SetColumn(0,"UDATE_TO", gfn_getWorkingDate(-2,"1000",ds_cond.GetColumn(0,"DATE_TO")));


	var st_gubn1 = "";
	var st_gubn2 = "";

	switch(ds_cond.GetColumn(0,"GUBN")) {
			case 1 : // JAMB
					 st_gubn1 =  " AND MM017.LIFNR IN ('3128538842','1268191379','3038140365') "                              
							   + " AND MM017.MATNR LIKE '361%' "
							   + " AND ((MM017.BOX_CK = '24' AND MM017.EXTWG = 'BOX11')  OR MM017.MAKTX LIKE 'JAMB__'||'%' ) ";
					 st_gubn2 =  " AND MM013.LIFNR IN ('3128538842','1268191379','3038140365') "
							   + " AND MM013.MATNR LIKE '361%' "
							   + " AND ((MM013.BOX_CK = '24' AND MM013.EXTWG = 'BOX11')  OR MM013.TXZ01 LIKE 'JAMB__'||'%' ) ";                           
					break;
			case 2 :  //MC BEAM
					 st_gubn1 =  " AND MM017.LIFNR IN ('1261322761','3038142140','1278167377','1378185450','1260263941') "
							   + " AND MM017.MATNR LIKE '103%' "
							   + " AND MM017.MAKTX LIKE 'MC BEAM'||'%' " ;
					 st_gubn2 =  " AND MM013.LIFNR IN ('1261322761','3038142140','1278167377','1378185450','1260263941') "
							   + " AND MM013.MATNR LIKE '103%' "
							   + " AND MM013.TXZ01 LIKE 'MC BEAM'||'%'  ";
					break;
			case 3 : //RAIL BRKT
					 st_gubn1 =  " AND MM017.LIFNR IN ('3030743848','1278167377') "
							   + " AND (MM017.MATNR LIKE '181%' OR MM017.MATNR LIKE '182%') "
							   + " AND MM017.MAKTX LIKE 'RAIL BRKT'||'%'    ";
					 st_gubn2 =  " AND MM013.LIFNR IN ('3030743848','1278167377') "
							   + " AND (MM013.MATNR LIKE '181%' OR MM013.MATNR LIKE '182%') "
							   + " AND MM013.TXZ01 LIKE 'RAIL BRKT'||'%'    ";
					break;
			case 4 :  //SILL S/T
					 st_gubn1 =  " AND MM017.LIFNR IN ('1278167377','5158122335') "
							   + " AND MM017.MATNR IN ('371001385','371001386','371001059')";
							   //+ " AND MM017.MAKTX LIKE 'SILL SPRT__'||'%'  ";
					 st_gubn2 =  " AND MM013.LIFNR IN ('1278167377','5158122335') "
							   + " AND MM013.MATNR IN ('371001385','371001386','371001059')";
							   //+ " AND MM013.TXZ01 LIKE 'SILL SPRT__'||'%'     ";
					break;
			case 5 :  //CABLE 
					 st_gubn1 =  " AND MM017.LIFNR IN ('3128126519','1178113200') "
							   + " AND MM017.MATNR LIKE '259%' "
							   + " AND MM017.MAKTX LIKE 'CABLE ASSY'||'%'  ";
					 st_gubn2 =  " AND MM013.LIFNR IN ('3128126519','1178113200') "
							   + " AND MM013.MATNR LIKE '259%' "
							   + " AND MM013.TXZ01 LIKE 'CABLE ASSY'||'%'     ";
					break;
			default :  //ROPE
					 st_gubn1 =  " AND MM017.LIFNR IN ('3030127389','1218118373') "
							   + " AND MM017.MATNR LIKE 'R15%' ";
					 st_gubn2 =  " AND MM013.LIFNR IN ('3030127389','1218118373') "
							   + " AND MM013.MATNR LIKE 'R15%' ";
					break;
		}

		ds_cond.SetColumn(0,"STGUBN1",st_gubn1);
		ds_cond.SetColumn(0,"STGUBN2",st_gubn2);

	if (length(ed_posid.value) > 0 && length(ed_posid.value) < 6){
		gfn_showAlert("CW00092");
		ed_posid.SetFocus();
		return;
	}

	tit_clearActionInfo();

	if(ds_cond.GetColumn(0,"GUBN") == "1")
	{
	    if(ds_cond.GetColumn(0,"DGBN") == "1")
	    {
			tit_addSearchActionInfo("mm04:MM0401003A_S4");

		} else {

			 tit_addSearchActionInfo("mm04:MM0401003A_S3");
		}
	} else {
		tit_addSearchActionInfo("mm04:MM0401003A_S1");
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}
