// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {

	// if(ds_cond.GetColumn(0,"GUBN") = "1")
	// {
		// if(ds_cond.GetColumn(0,"DGBN") = "2")
		// {
			// grd_list.SetFormat("dgbn_2_jamb");
		// } else {
			// grd_list.SetFormat("Format_jamb");
		// }
	// } else {
		// grd_list.SetFormat("default");
	// }

	if(ds_cond.GetColumn(0,"GUBN") = "1")
	{
		grd_list.SetFormat("dgbn_2_jamb");
	} else {
		grd_list.SetFormat("default");
	}

	// 조회 결과가 있으면 엑셀 버튼 활성화
	if ( ds_list.rowCount() > 0 ) {
		ufn_enableBtn("excel", true);
		gfn_showMsg("CI00002", ds_list.rowCount() + "");
	} else {
		ufn_enableBtn("excel", false);
//		cal_rdate_p.Enable = false;
		gfn_showMsg("CW00010");
	}
}


function fn_afterSave(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
