function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		return;
	} else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_multiSortGrid(obj, false, 1);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}



function btn_Hogi_OnClick(obj)
{
	var hogin = gfn_openPopup("MM0901004A", true);
	ed_posid = hogin[0];
}

var col = "";
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = rect[1] +100 ;
	cal_temp.Left = rect[0]; 
	cal_temp.Visible = true;


	if(ds_cond.GetColumn(0,"GUBN") == "1")
	{

	    if(ds_cond.GetColumn(0,"DGBN") == "2")
	    {

			if (nCell = 13)
			{
				cal_temp.value = ds_list.getColumn(nRow, "CESDZIDT");	
				col = "CESDZIDT";
			}
	    } else {
			alert("2");
			if (nCell = 14)
			{
				cal_temp.value = ds_list.getColumn(nRow, "CESDZJSD");	
				col = "CESDZJSD";
			}		
			else if (nCell = 15)
			{
				cal_temp.value = ds_list.getColumn(nRow, "CESDZJFD");	
				col = "CESDZJFD";
			}		
			else if (nCell = 16)
			{
				cal_temp.value = ds_list.getColumn(nRow, "CESDZTSD");	
				col = "CESDZTSD";
			}		
			else if (nCell = 18)
			{
				cal_temp.value = ds_list.getColumn(nRow, "CESDZIDT");	
				col = "CESDZIDT";
			}	
		}
	} else {                     // jamb이 아닌경우 
		if (nCell = 12)
		{
			cal_temp.value = ds_list.getColumn(nRow, "CESDZJDT");	
			col = "CESDZJDT";
		} else if (nCell = 14)
		{
			cal_temp.value = ds_list.getColumn(nRow, "CESDZIDT");	
			col = "CESDZIDT";
		}
	}	


	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	ds_list.setColumn(ds_list.row, col, strText);

	col = "";
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
	col = "";
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// if (ds_list.GetColumn(nRow,strColumnID) == ds_list.GetOrgBuffColumn(nRow,strColumnID))
	// {
		// return;
	// }

	ds_list.fireevent = false;

	if((ds_list.GetColumn(nRow,"CESDZJDT") == ds_list.GetOrgBuffColumn(nRow,"CESDZJDT") && 
	   ds_list.GetColumn(nRow,"CESDZIDT") == ds_list.GetOrgBuffColumn(nRow,"CESDZIDT") &&
	   ds_list.GetColumn(nRow,"CESDZMSY") == ds_list.GetOrgBuffColumn(nRow,"CESDZMSY") && 
	   ds_list.GetColumn(nRow,"CESDZBGO") == ds_list.GetOrgBuffColumn(nRow,"CESDZBGO") && 
	   ds_list.GetColumn(nRow,"CESDZJSD") == ds_list.GetOrgBuffColumn(nRow,"CESDZJSD") && 
	   ds_list.GetColumn(nRow,"CESDZJFD") == ds_list.GetOrgBuffColumn(nRow,"CESDZJFD") && 
	   ds_list.GetColumn(nRow,"CESDZTSD") == ds_list.GetOrgBuffColumn(nRow,"CESDZTSD") && 
	   ds_list.GetColumn(nRow,"CESDZPSG") == ds_list.GetOrgBuffColumn(nRow,"CESDZPSG")) ||
	   (ds_list.GetColumn(nRow,"FLAG1") == "I" && length(ds_list.GetColumn(nRow,"CESDZJDT")) == 0 && 
	     length(ds_list.GetColumn(nRow,"CESDZIDT")) == 0  &&
	     length(ds_list.GetColumn(nRow,"CESDZMSY")) == 0  && 
	     length(ds_list.GetColumn(nRow,"CESDZBGO")) == 0 && 
	     length(ds_list.GetColumn(nRow,"CESDZJSD")) == 0 && 
	     length(ds_list.GetColumn(nRow,"CESDZJFD")) == 0 && 
	     length(ds_list.GetColumn(nRow,"CESDZTSD")) == 0 ) ) {

		ds_list.SetColumn(nRow,"CHECK","0");
		ds_list.SetColumn(nRow,"FLAG","");
	} else {
		ds_list.SetColumn(nRow,"CHECK","1");

		if(ds_list.GetColumn(nRow,"FLAG1") == "I")
		{
			ds_list.SetColumn(nRow,"FLAG","I");
		} else
		{
			ds_list.SetColumn(nRow,"FLAG","U");
		}

	}

	ds_list.fireevent = true;
}


function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	// 보고서 유형 항목 클릭 시
	if(nCell==7){
		var rep = ds_list.GetColumn(nRow, "REP");
		var pjt = ds_list.GetColumn(nRow, "PJT");
		var hno = ds_list.GetColumn(nRow, "HNO");

		fn_goNextForm(rep,pjt,hno);
	}		
}


function fn_goNextForm(rep,pjt,hno){
	var formID="";

	switch (rep) {
		case 'H':
		case 'W':
			formID = "PS1101003B";
			break;			
		case 'J':
		case 'G':
			formID = "PS1102003B";
			break;			
		case 'S':
			formID = "PS1102001B";
			break;
		case 'C':
			formID = "PS1101007B";
			break;
		case 'D':
			formID = "PS1101010B";
			break;
		case 'F':
			formID = "PS1101009B";
			break;
		case 'R':
			formID = "PS1101005B";
			break;
		case 'N':
			formID = "PS1102006A";
			break;
		case 'X':
			formID = "PS1101011B";
			break;
		case 'Y':
			formID = "PS1101012B";
			break;

		case 'NH':
			formID = "PS1107001B";
			break;		

		case 'NX':
			formID = "PS1107002B";
			break;	

		case 'NC':
			formID = "PS1107003B";
			break;	

		case 'NR':
			formID = "PS1107004B";
			break;

		case 'NY':
			formID = "PS1109001B";
			break;

		case 'NZ':
			formID = "PS1110001B";
			break;

	}

	var arg = "PRE_PJT="+quote(pjt);
		arg += " PRE_HNO="+quote(hno);

	SELFLG = "N";
	gfn_openPopup(formID, true, arg);
	if(SELFLG == "Y") fn_query();
}
