function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	// 현재 조회 일자 설정 
	cal_erdat_fm.value = gfn_getCurrDate();
	//cal_erdat_fm.value = gfn_getBeforeDate(cal_erdat_to.value, 7);
	//cal_erdat_fm.value = gfn_getCurrDate();
	//  지역구분
	cb_ztype.Index = 1;

	// 운송 실행사 검색 제한 설정(특정 ID로만)
	// * 로지만
	if(g_user_id == "7002360" || g_user_id == "7001032" || g_user_id == "7001022" || g_user_id == "7002359" || g_user_id == "7001030") {
		cb_lifcd.Enable = false;
		cb_lifcd.Index = 1;
		st_delConfirm.Visible = true;
		ed_delConfirm.Visible = true;
		btn_delConfirm.Visible = true;
	// * 신원만
	}else if(g_user_id == "7003931" || g_user_id == "7003932" || g_user_id == "7003933" || g_user_id == "7003934" || g_user_id == "7003936" || g_user_id == "7003937") {
		cb_lifcd.Enable = false;
		cb_lifcd.Index = 2;
		st_delConfirm.Visible = true;
		ed_delConfirm.Visible = true;
		btn_delConfirm.Visible = true;
	}
	// 이재봉팀원 권한(엘리베이터 아님) 
	else if(g_user_id == "7000509" || g_user_id == "2017814" || g_user_id == "2022532") 
	{
		st_delConfirm.Visible = true;
		ed_delConfirm.Visible = true;
		btn_delConfirm.Visible = true;
	}

   // 대지역코드 ,배차 SMS전송 권한자 DataSet 가져오기
    var codeArr = ["PS003","LESMS"];
    var dsObjArr = [ds_zzactss,ds_sms];
    var addTypeArr = [G_CODE_NONE,G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0501001A_S2");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_zlzone_cd=ds_zlzone_cd"
        , ""
        , "fn_cdAfterQuery"
        , true);

    // 엑셀 버튼 비활성화
	ufn_enableBtn("excel", false);
	ufn_enableBtn("save", false);
	ufn_enableBtn("cancel", false);

	gfn_showMsg("CI00007");


	fv_url = "MM0501001A";	//"WNT_grdContents"; 

    lfn_setGirdContents("grd_list", grd_list.Format);

}
