function fn_query() 
{
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	//http.TimeOut = 1800;
	ds_cond.SetColumn(0,"PJT",trim(ed_pjt.value));


	if (length(Trim(ed_pjt.value)) > 0 && length(Trim(ed_pjt.value)) < 6) 
	{
		gfn_showMsg("CW00092");
		ed_pjt.SetFocus();
		return;
	}


	if(G_VEND_CODE == "3148131696" || G_VEND_CODE == "1018194310" || G_VEND_CODE == "3142189419" || g_user_id == "7000509")
	{
		ds_cond.SetColumn(0, "LIFNR", "0000");
	}
	else
	{
		ds_cond.SetColumn(0, "LIFNR", "");
	}
	// alert(G_VEND_CODE);
	// 업체 정보 로지 3148131696
	//			신원 1018194310


	if (length(trim(ed_pjt.value)) > 5 )
	{
		tit_clearActionInfo();
		tit_addSearchActionInfo("mm05:MM0501001A_S3");
		tit_addSearchActionInfo("mm05:MM0501001A_S4");
		tit_addSearchActionInfo("mm05:MM0501001A_S6");
		tit_addSearchActionInfo("mm05:MM0501001A_S8");

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list ds_sum=ds_sum ds_sum2=ds_sum2 ds_delConfirm=ds_delConfirm"
			, ""
			, "fn_afterQuery"
			, true);
	}
	else
	{
		// 필수 입력 항목 체크 
		if ( gfn_isNull(cal_erdat_fm, true) ) 
		{
			gfn_showMsg("CW00001","배차일자 시작");
			return;
		}

		// if ( gfn_isNull(cal_erdat_to, true) ) 
		// {
			// gfn_showMsg("CW00001","배차일자 종료");
			// return;
		// }

		// var maxdate = gfn_getAfterDate(cal_erdat_fm.value, 30);
		// var message_arg = ["조회기간","30일"];
		// if (cal_erdat_to > maxdate)
		// {
			// gfn_showAlert("CW00133",message_arg);
			// return;
		// }		



		tit_clearActionInfo();
		tit_addSearchActionInfo("mm05:MM0501001A_S1");
		tit_addSearchActionInfo("mm05:MM0501001A_S4");
		tit_addSearchActionInfo("mm05:MM0501001A_S6");
		tit_addSearchActionInfo("mm05:MM0501001A_S7");
		tit_addSearchActionInfo("mm05:MM0501001A_S8");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
