}


/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	// 테이블 별로 분리 하여 담기
	ds_list_0.ClearData();
	ds_list_1.ClearData();

	//var err[2];

	if (!gfn_isChangeDs(ds_list, true) ) {
		return;
	}

	if (!gfn_showConfirm("CI00008")) {
		return;
	}

	ds_list.fireevent = false;	
	gfn_copyDs(ds_list, ds_list_0, "GUBN =='2' && CHECK =='1'");
	gfn_copyDs(ds_list, ds_list_1, "GUBN =='6' && CHECK =='1'");

	ds_list.fireevent = true;

	tit_clearActionInfo();

	// 저장 처리 
	tit_addSaveActionInfo(
        ""
        , "mm05:MM0501001A_U1"
        , ""
        , "FLAG"
		,""
		, 0
		, "");
	tit_addSaveActionInfo(
        ""
        , "mm05:MM0501001A_U2"
        , ""
        , "FLAG"
		,""
		, 0
		, "");


	tit_callService(
        ""
        , ""
        , "ds_list_0=ds_list_0:A ds_list_1=ds_list_1:A"
        , ""
        , ""
        , "fn_afterSave");
}
