	ds_zlzone_cd.insertRow(0);
	ds_zlzone_cd.setColumn(0, "CODE", "");
	ds_zlzone_cd.setColumn(0, "CODE_NAME", "- 전 체 -");
	//  지역
	cb_zlzone.Index = 0;
}

