#include "LIB::commons.js";	// 공통 스크립트 
#include "LIB::common_hdel.js";	// 공통 스크립트  /* GNY 추가 */
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var isFirst;
var INIT_LIFNR;
var INIT_ZTON;
var FIRST_ERDAT;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();
	gfn_initDs(ds_cond, true);
	ck_input.value = 0;
	ck_fullset.Value = 0;
	ck_fullset_1.Value = 0;
	ch_ztrcost.FalseValue = "";

    // 조회 조건 설정
	if ( isExistVar("fa_zknum", true) ) 
	{
		ds_cond.SetColumn(0,"ZKNUM", fa_zknum);
		ds_cond.SetColumn(0,"ZKNUM2", fa_zknum2);

		if (substr(fa_pspid,0,2) == "QM")
		{
			ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,2,6));
		}
		else if (substr(fa_pspid,0,1) == "Q")
		{
			ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,1,6));
		}
		else
		{
			ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,0,6));
		}

		tit_clearActionInfo();
		tit_addSearchActionInfo("mm05:MM0502001B_S6");

		tit_callService(
			""
			, ""
			, ""
			, "ds_area=ds_area"
			, ""
			, "fn_cdAfterQuery"
			, true);
	} 
	else 
	{
		gfn_showAlert("CW00010");
	    close();
	}  
}


/********************************************************************************
* 기      능   : Callback  
********************************************************************************/
function fn_cdAfterQuery(errCode, errMsg) 
{
	fn_query();
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) 
{
	return gfn_isFormUnload(obj);
}
//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{

	tit_clearActionInfo();
	if (length(fa_tabnm) > 0) 
	{
		tit_addSearchActionInfo("mm05:MM0501003B_S1", null, true);
		tit_addSearchActionInfo("mm05:MM0502001B_S5", null, true);
		tit_addSearchActionInfo("mm05:MM0501003B_S3", null, true);
		tit_addSearchActionInfo("mm05:MM0501003B_S4", null, true);
	}
	else
	{
		tit_addSearchActionInfo("mm05:MM0501003B_S2", null, true);
		tit_addSearchActionInfo("mm05:MM0502001B_S5", null, true);
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond ds_date_=ds_date_"
        , "ds_list=ds_list ds_addr=ds_addr ds_list2=ds_list2 ds_list4=ds_list4"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
    // 조회 결과가 없으면 닫기
	if ( ds_list.rowCount() > 0 ) 
	{
		ds_list_org.Copy(ds_list);
        if (ds_list.GetColumn(0,"ZFULLSET") == "X")
        {
            ck_fullset.Value = "1";
        }
        if (ds_list.GetColumn(0,"ZFULLSET") == "Y")
        {
            ck_fullset_1.Value = "1";
        }


        // 직발주인 경우만 하단 그리드가  보이도록
        if (length(fa_tabnm) > 0) 
		{
			var popUp  = this;	
			popUp.window.height=595;	
		}
		else
		{
			var popUp  = this;	
			popUp.window.height=384;			
		}	


		// 최초 운송업체
		INIT_LIFNR = ds_list.GetColumn(0, "LIFNR");

		// 최초 차량수
		INIT_ZTON = parseInt(ds_list.getcolumn(0, "ZTON1")) +
					parseInt(ds_list.getcolumn(0, "ZTON2")) +
					parseInt(ds_list.getcolumn(0, "ZTON3")) +
					parseInt(ds_list.getcolumn(0, "ZTON4")) +
					parseInt(ds_list.getcolumn(0, "ZTON5")) +
					parseInt(ds_list.getcolumn(0, "ZTON6")) +
					parseInt(ds_list.getcolumn(0, "ZTON7")) +
					parseInt(ds_list.getcolumn(0, "ZTON8")) +
					parseInt(ds_list.getcolumn(0, "ZTON9")) +
					parseInt(ds_list.getcolumn(0, "ZTON10")) +
					parseInt(ds_list.getcolumn(0, "ZTON11")) +
					parseInt(ds_list.getcolumn(0, "ZTON12")) +
					parseInt(ds_list.getcolumn(0, "ZTON13")) +
					parseInt(ds_list.getcolumn(0, "ZTON14")) +
					parseInt(ds_list.getcolumn(0, "ZTON15"));


		dslist_copy.Copy(ds_list);


		isFirst = "Y";

		if ( ds_list7.rowCount() == 0 ) 	{
			ds_list7.AddRow();
			var arr = Split(ds_list4.GetColumn(0, "TPLSTDTQTY1"), ",");						
			for ( var j = 0; j< 15; j++)	{
				ds_list7.setColumn(0,"BEZEI1ZTON"+(parseInt(j)+1),arr[j]);						
			}

			var arr2 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY2"), ",");				
			for ( var j = 0; j< 15; j++)	{
				ds_list7.setColumn(0,"BEZEI2ZTON"+(parseInt(j)+1),arr2[j]);			
			}

			var arr3 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY3"), ",");				
			for ( var j = 0; j< 15; j++)	{
				ds_list7.setColumn(0,"BEZEI3ZTON"+(parseInt(j)+1),arr3[j]);			
			}

			var arr4 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY4"), ",");				
			for ( var j = 0; j< 15; j++)	{
				ds_list7.setColumn(0,"BEZEI4ZTON"+(parseInt(j)+1),arr4[j]);			
			}

			var arr5 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY5"), ",");				
			for ( var j = 0; j< 15; j++)	{
				ds_list7.setColumn(0,"BEZEI5ZTON"+(parseInt(j)+1),arr5[j]);		
			}

			var arr6 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY6"), ",");				
			for ( var j = 0; j< 15; j++)	{
				ds_list7.setColumn(0,"BEZEI6ZTON"+(parseInt(j)+1),arr6[j]);		
			}

			var arr7 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY7"), ",");				
			for ( var j = 0; j< 15; j++)	{
				ds_list7.setColumn(0,"BEZEI7ZTON"+(parseInt(j)+1),arr7[j]);		
			}
		}


		//trace(ds_list7.SaveXML());

		FIRST_ERDAT = cal_erdat.Text;
	} 
	else 
	{
		gfn_showAlert("CW00010");
		close();
	}
}

/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_save() 
{
	/*
	저장전 validation 체크
	1. 그리드의 출발지가 선택되어 있는데 차량 총수량과 톤수정보가 입력이 되지 않은 경우 체크
	2. 톤정보 전체합과 출발지상세정보 차량총수량 합계가 일치하는지 확인
	*/

	if (length(fa_tabnm) > 0) 
	{
		for(var i=0; i < 7 ; i++) {				
			var iChk =0;			
			if(length(trim(grd_list3.GetCellValue(i, 2) )) != 0) iChk++;
			if(length(trim(grd_list3.GetCellValue(i, 3) )) != 0) iChk++;
			if(length(trim(grd_list3.GetCellValue(i, 4) )) != 0) iChk++;


			if(iChk != 0 && iChk != 3)	{	
				gfn_showAlert( (i+1) + " 행의 출발지, 차량총수량, 톤수정보를 모두 입력해주세요." );			
				return;
			}
		}



		// 각각의 톤수 정보 상단 하단 일치 여부 확인		
		var zt1 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON1")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON1")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON1")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON1")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON1")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON1")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON1")) ;
		if(	parseInt(ed_zton1.value) != zt1) {
			gfn_showAlert("상단에서 입력한 1t 차량 수량과\n출발지 상세정보에서 입력된 1t 차량 총수량이 일치하지 않습니다.");		
			return;
		}		

		var zt2 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON2")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON2")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON2")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON2")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON2")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON2")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON2")) ;
		if(	parseInt(ed_zton2.value) != zt2) {
			gfn_showAlert("상단에서 입력한 2.5t 차량 수량과\n출발지 상세정보에서 입력된 2.5t 차량 총수량이 일치하지 않습니다.");		
			return;
		}

		var zt3 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON3")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON3")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON3")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON3")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON3")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON3")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON3")) ;
		if(	parseInt(ed_zton3.value) != zt3) {
			gfn_showAlert("상단에서 입력한 3.5t 차량 수량과\n출발지 상세정보에서 입력된 3.5t 차량 총수량이 일치하지 않습니다.");		
			return;
		}

		var zt4 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON4")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON4")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON4")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON4")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON4")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON4")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON4")) ;
		if(	parseInt(ed_zton4.value) != zt4) {
