					  parseInt(grd_list3.GetCellValue(3, 3)) + parseInt(grd_list3.GetCellValue(4, 3)) + parseInt(grd_list3.GetCellValue(5, 3)) + 
					  parseInt(grd_list3.GetCellValue(6, 3)); 


