function fn_checkLifnr(errCode, errMsg) 
{
	//trace(ds_list.SaveXML());
	tit_clearActionInfo();

    trace(ds_list.SaveXML());
	tit_callService(
        "MM0501003B_ACT1"
        , "save"
        , "ds_list=ds_list"
        , ""
        , ""
        , "fn_afterSave"
        , false);
}






/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) 
{

	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
		var arr = 1;
		close(arr);
	}
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() 
{
	close(null);
}

