	}	
}


function grd_list3_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{	
	if(nCell == 2)	{


		var arg = "comName=''";
		var rtn = gfn_openPopup("WB0101001C",true,arg);

		if ( rtn != null ) 
		{
			//ds_cond.setColumn(0, "USERCODE", rtn[0]);
			//ed_userCode_s.value = rtn[1];
			ds_list2.setColumn(nRow,'BEZEI',rtn[0]);	
			ds_list2.setColumn(nRow,'BEZEI_NM',rtn[1]);		
		}



		/*
		// 협력업체조회 팝업화면 OPEN
		var arg = "fa_lifnr= " 	+ quote("")   // 거래코드
				 + " fa_name1 = " + quote("")   // 협력업체명
				 ;
		var arr = gfn_openPopUpUrlHdel("MM12::MM1201004B.xml", true, arg );	

		if (arr != null && arr != 0)	{
			//trace("값은 "+arr[1]+"/"+arr[0]);
			//obj.value = arr[2];	// 리턴된 협력업체코드로 검색조건 SET

			ds_list.setColumn(nRow,'BEZEI',rtn[0]);	
			ds_list.setColumn(nRow,'BEZEI_NM',rtn[1]);				
		}
		*/
	}

	if(nCell == 3)	{
		tit_clearActionInfo();

		var arg2 = "seq_n= "+quote(nRow);
		var rtn2 = gfn_openPopup("MM0501003E",true,arg2);

		if ( rtn2 != null ) 
		{
			//alert("리턴값 " + rtn2[0]);
			// 총합계,  텍스트 글자,   delimeter 구분 
			//ds_cond.setColumn(0, "USERCODE", rtn[0]);
			//ed_userCode_s.value = rtn[1];
			//ds_list6.setColumn(nRow,'BEZEI',rtn[0]);	
			//ds_list6.setColumn(nRow,'BEZEI_NM',rtn[1]);	
			ds_list2.setColumn(nRow, "CARQT", rtn2[0]);	// 차량총수량 	
			ds_list2.setColumn(nRow, "ZTON", rtn2[1]);		// 텍스트 
			ds_list2.setColumn(nRow, "CAREACH", rtn2[2]);					
		}		
	}	

	if(nCell == 5)	{		

		fn_rowDelete(nRow);		

	}	
}




