	zton5 = parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON5"));// - parseInt(ds_list7.getcolumn(0,"BEZEI2ZTON5"));
	zton6 = parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON6"));// - parseInt(ds_list7.getcolumn(0,"BEZEI2ZTON6"));
	zton7 = parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON7"));// - parseInt(ds_list7.getcolumn(0,"BEZEI2ZTON7"));
	zton8 = parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON8"));// - parseInt(ds_list7.getcolumn(0,"BEZEI2ZTON8"));
