	zton15 = parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON15"));

	var vSum = parseInt(zton1) + parseInt(zton2) + parseInt(zton3) + parseInt(zton4) + parseInt(zton5) 
				+ parseInt(zton6) + parseInt(zton7) + parseInt(zton8) + parseInt(zton9) + parseInt(zton10)
				+ parseInt(zton11) + parseInt(zton12) + parseInt(zton13) + parseInt(zton14) + parseInt(zton15);

	if(vSum == "0")	{
		vSum = '';
	}	
	ds_list2.SetColumn(0, "CARQT", vSum); // 천안 차량 총수량 셋팅	


	// 다른 지역 셋업 
	var arr = Split(ds_list2.GetColumn(0, "CAREACH"), ",");						
	for ( var j = 0; j< 15; j++)	{
		ds_list7.setColumn(0,"BEZEI1ZTON"+(parseInt(j)+1),arr[j]);						
	}

	var arr2 = Split(ds_list2.GetColumn(1, "CAREACH"), ",");						
