function ck_InputYn_OnClick(obj,strValue)
{
	if(ck_InputYn.Value == "0")
	{
		grd_list3.Enable = false;
	}
	else
	{
		grd_list3.Enable = true;
	}
}


function fn_chgSum(nRow)
{	
	var arr = Split(ds_list2.GetColumn(nRow, "CAREACH"), ",");

	var sum1;
	for ( var i = 0; i < arr.length(); i ++ ) {
	//trace(arr[i]);	
		sum1 += (parseInt(arr[i]));
	}
	if(sum1 == "0"){
		sum1 = '';
	}	
	ds_list2.setColumn(nRow,'CARQT',sum1);	

}

function btn_Getton_OnClick(obj)
{ 
	if( trim(dslist_copy.getcolumn(0, "ZTON1")) == trim(ds_list.getcolumn(0, "ZTON1")) &&
		trim(dslist_copy.getcolumn(0, "ZTON2")) == trim(ds_list.getcolumn(0, "ZTON2")) &&
		trim(dslist_copy.getcolumn(0, "ZTON3")) == trim(ds_list.getcolumn(0, "ZTON3")) &&
		trim(dslist_copy.getcolumn(0, "ZTON4")) == trim(ds_list.getcolumn(0, "ZTON4")) &&
		trim(dslist_copy.getcolumn(0, "ZTON5")) == trim(ds_list.getcolumn(0, "ZTON5")) &&
		trim(dslist_copy.getcolumn(0, "ZTON6")) == trim(ds_list.getcolumn(0, "ZTON6")) &&
		trim(dslist_copy.getcolumn(0, "ZTON7")) == trim(ds_list.getcolumn(0, "ZTON7")) &&
		trim(dslist_copy.getcolumn(0, "ZTON8")) == trim(ds_list.getcolumn(0, "ZTON8")) &&
		trim(dslist_copy.getcolumn(0, "ZTON9")) == trim(ds_list.getcolumn(0, "ZTON9")) &&
		trim(dslist_copy.getcolumn(0, "ZTON10")) == trim(ds_list.getcolumn(0, "ZTON10")) &&
		trim(dslist_copy.getcolumn(0, "ZTON11")) == trim(ds_list.getcolumn(0, "ZTON11")) &&
		trim(dslist_copy.getcolumn(0, "ZTON12")) == trim(ds_list.getcolumn(0, "ZTON12")) &&
		trim(dslist_copy.getcolumn(0, "ZTON13")) == trim(ds_list.getcolumn(0, "ZTON13")) &&
		trim(dslist_copy.getcolumn(0, "ZTON14")) == trim(ds_list.getcolumn(0, "ZTON14")) &&
		trim(dslist_copy.getcolumn(0, "ZTON15")) == trim(ds_list.getcolumn(0, "ZTON15")) )	
	{
		gfn_showalert("차량 톤수 정보가 변경된 내용이 없습니다.");
		return;	
	}


	if(	length(trim(ed_zton1.value)) == 0  && length(trim(ed_zton2.value)) == 0 
		&& length(trim(ed_zton3.value)) == 0 && length(trim(ed_zton4.value)) == 0 
		&& length(trim(ed_zton5.value)) == 0 && length(trim(ed_zton6.value)) == 0 
		&& length(trim(ed_zton7.value)) == 0 && length(trim(ed_zton8.value)) == 0 
		&& length(trim(ed_zton9.value)) == 0 && length(trim(ed_zton10.value)) == 0 
		&& length(trim(ed_zton11.value)) == 0 && length(trim(ed_zton12.value)) == 0 
		&& length(trim(ed_zton13.value)) == 0 && length(trim(ed_zton14.value)) == 0 
		&& length(trim(ed_zton15.value)) == 0 )		
	 {
		gfn_showalert("상단에 입력된 톤수 정보가 없습니다.");
		return;	
	 }	

	 fn_setton();	
}

function fn_setton()
{	
	// 출하예정 상세 팝업 화면의 톤정보가 이전값과 변경이 되었는지 여부 확인

	if( trim(dslist_copy.getcolumn(0, "ZTON1")) <> trim(ds_list.getcolumn(0, "ZTON1")) ||
		trim(dslist_copy.getcolumn(0, "ZTON2")) <> trim(ds_list.getcolumn(0, "ZTON2")) ||
		trim(dslist_copy.getcolumn(0, "ZTON3")) <> trim(ds_list.getcolumn(0, "ZTON3")) ||
		trim(dslist_copy.getcolumn(0, "ZTON4")) <> trim(ds_list.getcolumn(0, "ZTON4")) ||
		trim(dslist_copy.getcolumn(0, "ZTON5")) <> trim(ds_list.getcolumn(0, "ZTON5")) ||
		trim(dslist_copy.getcolumn(0, "ZTON6")) <> trim(ds_list.getcolumn(0, "ZTON6")) ||
		trim(dslist_copy.getcolumn(0, "ZTON7")) <> trim(ds_list.getcolumn(0, "ZTON7")) ||
		trim(dslist_copy.getcolumn(0, "ZTON8")) <> trim(ds_list.getcolumn(0, "ZTON8")) ||
		trim(dslist_copy.getcolumn(0, "ZTON9")) <> trim(ds_list.getcolumn(0, "ZTON9")) ||
		trim(dslist_copy.getcolumn(0, "ZTON10")) <> trim(ds_list.getcolumn(0, "ZTON10")) ||
		trim(dslist_copy.getcolumn(0, "ZTON11")) <> trim(ds_list.getcolumn(0, "ZTON11")) ||
		trim(dslist_copy.getcolumn(0, "ZTON12")) <> trim(ds_list.getcolumn(0, "ZTON12")) ||
		trim(dslist_copy.getcolumn(0, "ZTON13")) <> trim(ds_list.getcolumn(0, "ZTON13")) ||
		trim(dslist_copy.getcolumn(0, "ZTON14")) <> trim(ds_list.getcolumn(0, "ZTON14")) ||
		trim(dslist_copy.getcolumn(0, "ZTON15")) <> trim(ds_list.getcolumn(0, "ZTON15")) )	
	{	
		//alert("변경 ");


			var z1 ,z2, z3, z4, z5, z6, z7, z8, z9, z10, z11, z12, z13, z14, z15; 
			z1 = parseInt(ds_list.getcolumn(0, "ZTON1")) - parseInt(dslist_copy.getcolumn(0, "ZTON1"));
			z2 = parseInt(ds_list.getcolumn(0, "ZTON2")) - parseInt(dslist_copy.getcolumn(0, "ZTON2"));
			z3 = parseInt(ds_list.getcolumn(0, "ZTON3")) - parseInt(dslist_copy.getcolumn(0, "ZTON3"));
			z4 = parseInt(ds_list.getcolumn(0, "ZTON4")) - parseInt(dslist_copy.getcolumn(0, "ZTON4"));
			z5 = parseInt(ds_list.getcolumn(0, "ZTON5")) - parseInt(dslist_copy.getcolumn(0, "ZTON5"));
			z6 = parseInt(ds_list.getcolumn(0, "ZTON6")) - parseInt(dslist_copy.getcolumn(0, "ZTON6"));
			z7 = parseInt(ds_list.getcolumn(0, "ZTON7")) - parseInt(dslist_copy.getcolumn(0, "ZTON7"));
			z8 = parseInt(ds_list.getcolumn(0, "ZTON8")) - parseInt(dslist_copy.getcolumn(0, "ZTON8"));
			z9 = parseInt(ds_list.getcolumn(0, "ZTON9")) - parseInt(dslist_copy.getcolumn(0, "ZTON9"));
			z10 = parseInt(ds_list.getcolumn(0, "ZTON10")) - parseInt(dslist_copy.getcolumn(0, "ZTON10"));
			z11 = parseInt(ds_list.getcolumn(0, "ZTON11")) - parseInt(dslist_copy.getcolumn(0, "ZTON11"));
			z12 = parseInt(ds_list.getcolumn(0, "ZTON12")) - parseInt(dslist_copy.getcolumn(0, "ZTON12"));
			z13 = parseInt(ds_list.getcolumn(0, "ZTON13")) - parseInt(dslist_copy.getcolumn(0, "ZTON13"));
			z14 = parseInt(ds_list.getcolumn(0, "ZTON14")) - parseInt(dslist_copy.getcolumn(0, "ZTON14"));
			z15 = parseInt(ds_list.getcolumn(0, "ZTON15")) - parseInt(dslist_copy.getcolumn(0, "ZTON15"));

			ds_list7.SetColumn(0,"BEZEI1ZTON1", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON1")) + parseInt(z1));					
			ds_list7.SetColumn(0,"BEZEI1ZTON2", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON2")) + parseInt(z2));			
			ds_list7.SetColumn(0,"BEZEI1ZTON3", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON3")) + parseInt(z3));		
			ds_list7.SetColumn(0,"BEZEI1ZTON4", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON4")) + parseInt(z4));		
			ds_list7.SetColumn(0,"BEZEI1ZTON5", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON5")) + parseInt(z5));					
			ds_list7.SetColumn(0,"BEZEI1ZTON6", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON6")) + parseInt(z6));					
			ds_list7.SetColumn(0,"BEZEI1ZTON7", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON7")) + parseInt(z7));				
			ds_list7.SetColumn(0,"BEZEI1ZTON8", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON8")) + parseInt(z8));				
			ds_list7.SetColumn(0,"BEZEI1ZTON9", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON9")) + parseInt(z9));					
			ds_list7.SetColumn(0,"BEZEI1ZTON10", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON10")) + parseInt(z10));					
			ds_list7.SetColumn(0,"BEZEI1ZTON11", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON11")) + parseInt(z11));
			ds_list7.SetColumn(0,"BEZEI1ZTON12", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON12")) + parseInt(z12));
			ds_list7.SetColumn(0,"BEZEI1ZTON13", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON13")) + parseInt(z13));
			ds_list7.SetColumn(0,"BEZEI1ZTON14", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON14")) + parseInt(z14));
			ds_list7.SetColumn(0,"BEZEI1ZTON15", parseInt(ds_list7.getcolumn(0,"BEZEI1ZTON15")) + parseInt(z15));



			strRtn3 = ds_list7.getcolumn(0,"BEZEI1ZTON1")+",";				
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON2")+",";				
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON3")+",";					
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON4")+",";			
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON5")+",";			
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON6")+",";			
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON7")+",";			
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON8")+",";			
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON9")+",";					
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON10")+",";
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON11")+",";
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON12")+",";
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON13")+",";
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON14")+",";
			strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON15");					

			ds_list2.SetColumn(0,"CAREACH",strRtn3);	


			var strRtn1;
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON1"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON1")) != "0")
				strRtn1 = " 1t-"+ds_list7.getcolumn(0,"BEZEI1ZTON1")+",";		
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON2"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON2")) != "0")
				strRtn1 += " 2.5t-"+ds_list7.getcolumn(0,"BEZEI1ZTON2")+",";		
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON3"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON3")) != "0")
				strRtn1 += " 3.5t-"+ds_list7.getcolumn(0,"BEZEI1ZTON3")+",";			
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON4"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON4")) != "0")
				strRtn1 += " 5t-"+ds_list7.getcolumn(0,"BEZEI1ZTON4")+",";			
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON5"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON5")) != "0")
				strRtn1 += " 8t-"+ds_list7.getcolumn(0,"BEZEI1ZTON5")+",";			
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON6"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON6")) != "0")
				strRtn1 += " 11t-"+ds_list7.getcolumn(0,"BEZEI1ZTON6")+",";			
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON7"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON7")) != "0")
				strRtn1 += " 18t-"+ds_list7.getcolumn(0,"BEZEI1ZTON7")+",";		
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON8"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON8")) != "0")
				strRtn1 += " 25t-"+ds_list7.getcolumn(0,"BEZEI1ZTON8")+",";			
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON9"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON9")) != "0")
				strRtn1 += " Trailer-"+ds_list7.getcolumn(0,"BEZEI1ZTON9")+",";			
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON10"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON10")) != "0")
				strRtn1 += " 택배-"+ds_list7.getcolumn(0,"BEZEI1ZTON10")+",";

			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON11"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON11")) != "0")
				strRtn1 += " Trail-20-"+ds_list7.getcolumn(0,"BEZEI1ZTON11")+",";
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON12"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON12")) != "0")
				strRtn1 += " Trail-40-"+ds_list7.getcolumn(0,"BEZEI1ZTON12")+",";
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON13"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON13")) != "0")
				strRtn1 += " Trail-20 왕복-"+ds_list7.getcolumn(0,"BEZEI1ZTON13")+",";
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON14"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON14")) != "0")
				strRtn1 += " Trail-40 왕복-"+ds_list7.getcolumn(0,"BEZEI1ZTON14")+",";
			if(length(trim(ds_list7.getcolumn(0,"BEZEI1ZTON15"))) != 0 && trim(ds_list7.getcolumn(0,"BEZEI1ZTON15")) != "0")
				strRtn1 += " 셔틀-"+ds_list7.getcolumn(0,"BEZEI1ZTON15")+",";						

			strRtn1 = strRtn1.Left(strRtn1.length-1);		

			ds_list2.SetColumn(0,"ZTON",strRtn1);	

			//alert("나중 BEZEI1ZTON1" + ds_list7.getcolumn(0,"BEZEI1ZTON1"));

	}	



	else{		//alert("변경안  됨 ");

		var strRtn3;	// delimeter 로 구분된 톤수정보 

		ds_list7.SetColumn(0,"BEZEI1ZTON1", parseInt(ed_zton1.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON2", parseInt(ed_zton2.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON3", parseInt(ed_zton3.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON4", parseInt(ed_zton4.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON5", parseInt(ed_zton5.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON6", parseInt(ed_zton6.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON7", parseInt(ed_zton7.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON8", parseInt(ed_zton8.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON9", parseInt(ed_zton9.value));
		ds_list7.SetColumn(0,"BEZEI1ZTON10", parseInt(ed_zton10.value));	

		strRtn3 = ds_list7.getcolumn(0,"BEZEI1ZTON1")+",";				
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON2")+",";				
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON3")+",";					
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON4")+",";			
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON5")+",";			
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON6")+",";			
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON7")+",";			
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON8")+",";			
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON9")+",";					
		strRtn3 += ds_list7.getcolumn(0,"BEZEI1ZTON10");					

		ds_list2.SetColumn(0,"CAREACH",strRtn3);		



		var strRtn2 = parseInt(ed_zton1.value)+parseInt(ed_zton2.value)+parseInt(ed_zton3.value)+parseInt(ed_zton4.value)+parseInt(ed_zton5.value)+
				 parseInt(ed_zton6.value)+parseInt(ed_zton7.value)+parseInt(ed_zton8.value)+parseInt(ed_zton9.value)+parseInt(ed_zton10.value);
		ds_list2.setColumn(0,'CARQT', strRtn2);


		var strRtn; 	// 텍스트로 연결된 톤수 정보
		if(ed_zton1.value != '' && ed_zton1.value != '0')
			strRtn = " 1t-"+ed_zton1.value+",";

		if(ed_zton2.value != '' && ed_zton2.value != '0')
			strRtn += " 2.5t-"+ed_zton2.value+",";

		if(ed_zton3.value != '' && ed_zton3.value != '0')
			strRtn += " 3.5t-"+ed_zton3.value+",";

		if(ed_zton4.value != '' && ed_zton4.value != '0')
			strRtn += " 5t-"+ed_zton4.value+",";

		if(ed_zton5.value != '' && ed_zton5.value != '0')
			strRtn += " 8t-"+ed_zton5.value+",";

		if(ed_zton6.value != '' && ed_zton6.value != '0')
			strRtn += " 11t-"+ed_zton6.value+",";

		if(ed_zton7.value != '' && ed_zton7.value != '0')
			strRtn += " 18t-"+ed_zton7.value+",";

		if(ed_zton8.value != '' && ed_zton8.value != '0')
			strRtn += " 25t-"+ed_zton8.value+",";

		if(ed_zton9.value != '' && ed_zton9.value != '0')
			strRtn += " Trailer-"+ed_zton9.value+",";

		if(ed_zton10.value != '' && ed_zton10.value != '0')
			strRtn += " 택배-"+ed_zton10.value+",";		

		strRtn = strRtn.Left(strRtn.length-1);


		ds_list2.setColumn(0,'ZTON', strRtn);		
	}


	dslist_copy.Copy(ds_list);
}


function btn_CalerdatChg_OnClick(obj)
{	
	if(btn_CalerdatChg.Text == "변경 취소")
	{
		close();
	}
	else
	{
		if(length(trim(Edit0.Text)) == 0)
		{
			ufn_CalerdatChg();
		}
		else
		{
			gfn_showInform("CW10025", "경유계획 번호가 있는 호차 정보는 수정이 불가합니다.");		
			return;
		}
	}


}

function ufn_CalerdatChg()
{
	tit_addSearchActionInfo("mm05:MM0501003B_S6", null, true);

	ds_cond3.setColumn(0,'ZKNUM', ed_pjt.Text);	

	tit_callService(
        ""
        , ""
        , "ds_cond3=ds_cond3"
        , "ds_list8=ds_list8"
        , ""
        , "fn_afterCalerdatChg"
        , true);

}

function fn_afterCalerdatChg()
{
	if(ds_list8.rowcount() == 1)
	{
		cal_erdat.Enable = true;
		div_btn.btn_save.enable = false;

		ed_zton1.Enable = false;	
		ed_zton2.Enable = false;
		ed_zton3.Enable = false;
		ed_zton4.Enable = false;
		ed_zton5.Enable = false;
		ed_zton6.Enable = false;
		ed_zton7.Enable = false;
		ed_zton8.Enable = false;
		ed_zton9.Enable = false;
		ed_zton10.Enable = false;
		ed_zton11.Enable = false;
		ed_zton12.Enable = false;
		ed_zton13.Enable = false;
		ed_zton14.Enable = false;
		ed_zton15.Enable = false;
		grd_list3.Enable = false;
		btn_Getton.Enable = false;
		ck_input.Enable = false;
		ck_fullset.Enable = false;
		ck_fullset_1.Enable = false;
		ch_ztrcost.Enable = false;
		cb_text.Enable = false;
		cb_area.Enable = false;
		ed_addr.Enable = false;
		ed_ztext.Enable = false;

		btn_CalerdatChg.Text = "변경 취소";


	}
	else
	{
		gfn_showInform("CW10025", "경유계획 번호가 있는 호차 정보는 수정이 불가합니다.");		
		return;
	}
}

function btn_CalerdatChgSave_OnClick(obj)
{
	if(FIRST_ERDAT == cal_erdat.Text)
	{
		gfn_showInform("CW10025", "배차일자가 변경되지 않아서 변경 저장할 내용이 없습니다.");		
		return;
	}
	else
	{
		ufc_ChgSave();
	}
}





/*
	아래 부터 배차일자 변경에 따른 기능 변경건
*/
function ufc_ChgSave() 
{
	/*
	저장전 validation 체크
	1. 그리드의 출발지가 선택되어 있는데 차량 총수량과 톤수정보가 입력이 되지 않은 경우 체크
	2. 톤정보 전체합과 출발지상세정보 차량총수량 합계가 일치하는지 확인
	*/

	if (length(fa_tabnm) > 0) 
	{
		for(var i=0; i < 7 ; i++) {				
			var iChk =0;			
			if(length(trim(grd_list3.GetCellValue(i, 2) )) != 0) iChk++;
			if(length(trim(grd_list3.GetCellValue(i, 3) )) != 0) iChk++;
			if(length(trim(grd_list3.GetCellValue(i, 4) )) != 0) iChk++;


			if(iChk != 0 && iChk != 3)	{	
				gfn_showAlert( (i+1) + " 행의 출발지, 차량총수량, 톤수정보를 모두 입력해주세요." );			
				return;
			}
		}



		// 각각의 톤수 정보 상단 하단 일치 여부 확인		
		var zt1 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON1")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON1")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON1")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON1")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON1")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON1")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON1")) ;
		if(	parseInt(ed_zton1.value) != zt1) {
			gfn_showAlert("상단에서 입력한 1t 차량 수량과\n출발지 상세정보에서 입력된 1t 차량 총수량이 일치하지 않습니다.");		
			return;
		}		

		var zt2 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON2")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON2")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON2")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON2")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON2")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON2")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON2")) ;
		if(	parseInt(ed_zton2.value) != zt2) {
			gfn_showAlert("상단에서 입력한 2.5t 차량 수량과\n출발지 상세정보에서 입력된 2.5t 차량 총수량이 일치하지 않습니다.");		
			return;
		}

		var zt3 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON3")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON3")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON3")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON3")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON3")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON3")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON3")) ;
		if(	parseInt(ed_zton3.value) != zt3) {
			gfn_showAlert("상단에서 입력한 3.5t 차량 수량과\n출발지 상세정보에서 입력된 3.5t 차량 총수량이 일치하지 않습니다.");		
			return;
		}

		var zt4 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON4")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON4")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON4")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON4")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON4")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON4")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON4")) ;
		if(	parseInt(ed_zton4.value) != zt4) {
			gfn_showAlert("상단에서 입력한 5t 차량 수량과\n출발지 상세정보에서 입력된 5t 차량 총수량이 일치하지 않습니다.");		
			return;
		}

		var zt5 = parseInt(ds_list7.GetColumn(0,"BEZEI1ZTON5")) + parseInt(ds_list7.GetColumn(0,"BEZEI2ZTON5")) + parseInt(ds_list7.GetColumn(0,"BEZEI3ZTON5")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON5")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON5")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON5")) 
				+ parseInt(ds_list7.GetColumn(0,"BEZEI7ZTON5")) ;
		if(	parseInt(ed_zton5.value) != zt5) {
